BEGIN
	SELECT DISTINCT(nt.codNotaSalida) AS codSalida, 
  CASE dt.codVenta WHEN 0 THEN dt.codCotizacion ELSE dt.codVenta END AS codVenta, nt.codTipoDocumento AS codDocumento, 
       td.descripcion AS NDocumento,
				 CONCAT(td.sigla,'-',nt.codNotaSalida) AS documento, nt.fechasalida, nt.fecharegistro, nt.codUsuario AS codUser,
				 nt.formapago AS codFormaPago, fp.descripcion AS FormaPago,  
         nt.moneda AS codMoneda, m.descripcion AS Moneda, ltap.codListaPrecio AS codLista, ltap.nombre AS NLista, nt.tipocambio
				 
	FROM notasalida nt INNER JOIN tipodocumento td ON nt.codTipoDocumento=td.codTipoDocumento
			 INNER JOIN formapago fp ON nt.formapago=fp.codFormaPago
			 INNER JOIN moneda m ON nt.moneda=m.codMoneda
			 INNER JOIN detallenotasalida dt ON nt.codNotaSalida=dt.codNotaSalida
			 INNER JOIN listaprecio ltap ON dt.codListaPrecio=ltap.codListaPrecio
			 INNER JOIN cliente cli ON nt.codCliente=cli.codCliente
       LEFT JOIN detallefactura_venta d ON d.codNotaSalida = nt.codNotaSalida
WHERE nt.estado=1 AND nt.codAlmacen=codalma AND dt.estado=1 AND cli.codCliente=codcliente AND
			dt.codNotaSalida NOT IN (SELECT dfv.codNotaSalida FROM detallefactura_venta dfv)
GROUP BY dt.codNotaSalida;

