, IFNULL(gur.codVehiculoTransporte,0), IFNULL(vt.codmarca,0), IFNULL(vt.codmodelo,0), IFNULL(vt.placa,""), IFNULL(mt.descripcion,"") as marca
, IFNULL(mdt.descripcion,"") as modelo, IFNULL(vt.constanciainscripcion,""),IFNULL(gur.codConductor,0), IFNULL(cdt.nombre,""), IFNULL(cdt.licencia,"")
, IFNULL(gur.codEmpresaTransporte,0),IFNULL(et.ruc,""), IFNULL(et.razonsocial,""), IFNULL(et.direccion,""), gur.fechaemision, gur.fechatraslado, IFNULL(gur.comentario,"")
, gur.estado, gur.facturado, gur.codUser, gur.fecharegistro, IFNULL(gur.codPedido,0), IFNULL(gur.codFactura,0)
FROM 		guiaremision gur
LEFT OUTER JOIN tipodocumento td ON gur.codDocumento = td.codTipoDocumento
LEFT OUTER JOIN serie se ON gur.codserie = se.codSerie
LEFT OUTER JOIN cliente cl ON gur.codCliente = cl.codCliente
