-- INNER JOIN detallefactura_venta AS DF ON F.codFacturaV = DF.codFacturaV
INNER JOIN tipodocumento as td ON td.codTipoDocumento = f.codTipoDocumento
INNER JOIN transaccion as tr ON tr.codTransaccion = f.codTransaccion
LEFT JOIN pago AS P ON F.codFacturaV = P.codNota AND p.ingresoegreso = 1 AND p.estado = 1

WHERE F.anulado = 0 AND F.estado = 1 AND F.cancelado = 0 AND F.codCliente = codCli AND f.pendiente > 0

