       nt.cancelado, nt.codUsuario, nt.fecharegistro, IFNULL(nt.codSerie,0), IFNULL(nt.serie,""), 
       IFNULL(nt.codReferencia,0), IFNULL(nt.flete,0), IFNULL( CONCAT(s.serie," ",orde.numeracion),0) as documentoOrden,
       IFNULL(nt.codalmacenemisor,0), 
