DROP PROCEDURE IF EXISTS `MuestraNotaIngresoOrden`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `MuestraNotaIngresoOrden`(finicial datetime, ffinal datetime)
BEGIN
SELECT DISTINCT (nt.codNotaIngreso) AS codNota, nt.CodOrdenCompra AS ordenCompra, nt.codTipoDocumento AS codDocumento, CONCAT(td.sigla,'-',nt.numdocumento) AS documento, 
       nt.fechaingreso, nt.fecharegistro, nt.codUsuario AS codUser,CONCAT(s.serie," - ",oc.numeracion) as docOrden, al.nombre as almacen ,pr.razonsocial as razonsocial
FROM notaingreso nt	
     INNER JOIN detallenotaingreso dtn ON dtn.codNotaIngreso=nt.codNotaIngreso
     INNER JOIN tipodocumento td ON nt.codTipoDocumento = td.codTipoDocumento
LEFT OUTER JOIN orden_compra oc on oc.CodOrdenCompra=nt.CodOrdenCompra
INNER JOIN serie s on s.codSerie=oc.codserie
INNER JOIN almacen al on al.codAlmacen=nt.codAlmacen
INNER JOIN proveedor pr ON pr.codProveedor=nt.codProveedor
WHERE nt.CodOrdenCompra IN (SELECT ordc.codOrdenCompra FROM orden_compra ordc WHERE nt.CodOrdenCompra=ordc.codOrdenCompra)
      /*AND nt.codAlmacen=codalma*/ AND nt.estado=1 AND nt.anulado=0 AND nt.codTransaccion=14 
and nt.codTipoDocumento<>2 and dtn.cantidadpendiente>0
      and date(nt.fechaingreso) BETWEEN finicial AND ffinal

UNION

SELECT DISTINCT (nt.codNotaIngreso) AS codNota, "0" AS ordenCompra, nt.codTipoDocumento AS codDocumento, 
CONCAT(td.sigla,'-',nt.numdocumento) AS documento, nt.fechaingreso, nt.fecharegistro, nt.codUsuario AS codUser, 
"0" as docOrden, al.nombre as almacen ,pr.razonsocial as razonsocial
FROM notaingreso nt	
     INNER JOIN detallenotaingreso dtn ON dtn.codNotaIngreso=nt.codNotaIngreso
     INNER JOIN tipodocumento td ON nt.codTipoDocumento = td.codTipoDocumento
INNER JOIN almacen al on al.codAlmacen=nt.codAlmacen
INNER JOIN proveedor pr ON pr.codProveedor=nt.codProveedor
WHERE nt.estado=1 AND nt.anulado=0 and nt.recibido=0 AND nt.codTransaccion=1
and nt.codTipoDocumento<>2 and dtn.cantidadpendiente>0
      and date(nt.fechaingreso) BETWEEN finicial AND ffinal;
-- ORDER BY CONCAT(oc.codserie," - ",oc.numeracion);

END
;;
DELIMITER ;

