CREATE DEFINER=`root`@`localhost` PROCEDURE `MuestraNotaIngresoSD`(codprov int(11), codalma int(11), finicial datetime, ffinal datetime)
BEGIN
	SELECT 
		  LPAD(ni.codNotaIngreso ,11,'0')AS codNota,
			CONCAT(tr.sigla, " - ", ni.numdocumento) AS doc, 
			pr.razonsocial,
			pr.ruc,
			al.descripcion AS almacen,
			ni.comentario,
			ni.fechaingreso AS fecha
	FROM 
		notaingreso ni INNER JOIN proveedor pv ON pv.codProveedor = ni.codProveedor
		INNER JOIN tipodocumento td ON ni.codTipoDocumento = td.codTipoDocumento
		INNER JOIN transaccion tr ON tr.codTransaccion = ni.codTransaccion
		INNER JOIN proveedor pr ON pr.codProveedor = ni.codProveedor
		INNER JOIN almacen al ON al.codAlmacen = ni.codAlmacen
	WHERE
			ni.codProveedor = codprov AND 
			ni.codAlmacen = codalma AND
		  (ni.codTransaccion = 1 OR ni.codTransaccion = 14) AND 
			DATE(ni.fechaingreso) BETWEEN DATE(finicial) AND DATE(ffinal);
END
;;
