SELECT  nt.codFactura as codigo, 1 as tipo,nt.DocumentoFactura as numdocumento, "" as docref, pr.codProveedor, pr.ruc, pr.razonsocial
				, nt.fechaingreso as fechaemision, nt.fechapago as fechavence,mon.descripcion AS moneda 
				, nt.total as total, nt.pendiente as pendiente, pr.banco, pr.ctacte, "" as num
				,(SELECT CASE esta WHEN 0 THEN "Ingresar Pago" WHEN 1 THEN "Muestra Pagos" END) AS accion
				,(SELECT COUNT(p.codPago) FROM pago p WHERE p.codNota = nt.codFactura AND p.ingresoegreso = 0  AND p.estado = 1) as cantpagos, nt.cancelado,nt.fechacancelado
	FROM facturacion nt INNER JOIN proveedor pr ON nt.codProveedor = pr.codProveedor	
											INNER JOIN almacen al ON nt.codAlmacen = al.codAlmacen
											INNER JOIN tipodocumento td ON nt.codTipoDocumento = td.codTipoDocumento
											INNER JOIN moneda mon on mon.codMoneda=nt.moneda
											INNER JOIN transaccion tr ON nt.codTransaccion = tr.codTransaccion ##codigo de IMGRESO POR COMPRA
	WHERE nt.cancelado = esta AND nt.estado = 1 AND al.codEmpresa = empre AND date(nt.fechaingreso) BETWEEN date(fecha1) AND date(fecha2) AND !ISNULL(formapago)

	UNION

	SELECT  lt.codLetra as codigo, 2 as tipo, CONCAT(td.sigla," - ",lt.numdocumento) as numdocumento, DocumentoFactura as docref 
				, pr.codProveedor, pr.ruc, pr.razonsocial, lt.fechaemision as fechaemision, lt.fechavencimiento as fechavence
				,mon.descripcion AS moneda ,lt.monto as total, lt.montopendiente as pendiente
				, bc.descripcion as banco, "", lt.numerounico as num,(SELECT CASE esta WHEN 0 THEN "Ingresar Pago" WHEN 1 THEN "Muestra Pagos" END) AS accion
				,(SELECT COUNT(p.codPago) FROM pago p WHERE p.codNota = lt.codNota  AND p.estado = 1) as cantpagos, lt.cancelado, lt.fechacancelado
	FROM letra lt 			LEFT OUTER JOIN facturacion nt ON nt.codFactura = lt.codNota
											LEFT OUTER JOIN proveedor pr ON lt.codLiberador = pr.codProveedor	
											INNER JOIN almacen al ON lt.codAlmacen = al.codAlmacen
											LEFT OUTER JOIN tipodocumento td ON lt.codDocumento = td.codTipoDocumento
											LEFT OUTER JOIN tipodocumento ttdd ON nt.codTipoDocumento = ttdd.codTipoDocumento
											LEFT OUTER JOIN banco bc ON lt.codBanco = bc.codBanco
											INNER JOIN moneda mon on mon.codMoneda=lt.codMoneda
	WHERE lt.cancelado = esta AND lt.estado = 1 AND al.codEmpresa = empre AND date(lt.fechaemision) BETWEEN date(fecha1) AND date(fecha2) AND lt.ingresoegreso = 0 

	ORDER BY fechavence, codigo DESC;
	


END
;;
DELIMITER ;

