				cu.cancelado, cu.fechacancelado
	FROM cuotaprestamo cu LEFT OUTER JOIN prestamobancario pb ON pb.codPrestamoBancario = cu.codPrestamoBancario
				LEFT OUTER JOIN banco b ON pb.codBanco = b.codBanco	
				INNER JOIN moneda mon on mon.codMoneda=pb.codMoneda
	WHERE cu.cancelado = esta AND cu.estado = 1
				AND date(cu.fechavencimiento) BETWEEN date(fecha1) AND date(fecha2)
	ORDER BY fechavence, codigo DESC;
ELSE
	SELECT cu.codCuotaPrestamo as codigo, pb.codPrestamoBancario AS codPrestamo, cu.nroCuota, 5 as tipo, pb.CodBanco, b.descripcion AS descBanco, 
				cu.fechaemision as fechaemision, cu.fechavencimiento as fechavence, mon.descripcion AS moneda,
				cu.monto as total, cu.montopendiente as pendiente,
				(SELECT CASE esta WHEN 0 THEN "Ingresar Pago" WHEN 1 THEN "Muestra Pagos" END) AS accion,
				(SELECT COUNT(p.codPago) FROM pago p WHERE p.codCuotaPreBan = cu.codCuotaPrestamo AND p.estado = 1) as cantpagos, 
				cu.cancelado, cu.fechacancelado
	FROM cuotaprestamo cu LEFT OUTER JOIN prestamobancario pb ON pb.codPrestamoBancario = cu.codPrestamoBancario
				LEFT OUTER JOIN banco b ON pb.codBanco = b.codBanco	
				INNER JOIN moneda mon on mon.codMoneda=pb.codMoneda
	WHERE cu.cancelado = esta AND cu.estado = 1
				AND date(cu.fechacancelado) BETWEEN date(fecha1) AND date(fecha2)
	ORDER BY fechavence, codigo DESC;
END IF;
END
;;
