	SELECT 	p.codProducto, p.referencia, p.descripcion,IFNULL(pa.stockdisponible,0) as stockdisponible, u.codUnidadMedida, u.descripcion, p.codControlStock, 
					p.precioconigv, p.igv,p.maxPorcDescto
	FROM 		producto p INNER JOIN unidadmedida u ON p.codUnidadMedida = u.codUnidadMedida
	LEFT OUTER JOIN productoalmacen pa ON p.codProducto = pa.codProducto AND pa.codAlmacen = codalm
	WHERE 	p.referencia = codpro and  pa.stockdisponible >0;

ELSEIF caso = 2 THEN

	IF lista = 0 THEN
		SELECT 	p.codProducto, p.referencia, p.descripcion,IFNULL(pa.stockdisponible,0) as stockdisponible, u.codUnidadMedida, u.descripcion, p.codControlStock,
						ROUND(IFNULL(p.preciocatalogo,0),v_de) as preciodolares,
						ROUND(IFNULL(p.preciocatalogo * v_tc,0),v_de) as preciosoles,
						IFNULL(pa.preciovariable,1),IFNULL(pa.oferta,0),IFNULL(pa.pcdescuento,0),IFNULL(pa.preciooferta,0), p.precioconigv, p.igv,
						IFNULL(pa.stockminimo,0), IFNULL(pa.stockreposicion,0), IFNULL(pa.stockmaximo,0),p.maxPorcDescto, eq.codUnidadEquivalente
		FROM 		producto p 
		INNER JOIN unidadmedida u ON p.codUnidadMedida = u.codUnidadMedida /* carlos ato */
		INNER JOIN unidadequivalente eq ON eq.codProducto = p.codProducto  /* carlos ato */
		LEFT OUTER JOIN productoalmacen pa ON p.codProducto = pa.codProducto AND pa.codAlmacen = codalm
		WHERE 	p.referencia = codpro AND eq.compra_venta = 0 and pa.stockdisponible >0;
	ELSE
		SELECT 	p.codProducto, p.referencia, p.descripcion,IFNULL(pa.stockdisponible,0) as stockdisponible, u.codUnidadMedida, u.descripcion, p.codControlStock,
						ROUND(IFNULL(IF(dl.precio=0,p.preciocatalogo,dl.precio),p.preciocatalogo),v_de) as preciodolares,
						ROUND(obtienepreciosoles_lista(v_de,IFNULL(if(dl.precio=0,p.preciocatalogo,dl.precio),IFNULL(p.preciocatalogo,0)) * v_tc),v_de) as preciosoles,
						IFNULL(pa.preciovariable,1),IFNULL(pa.oferta,0),IFNULL(pa.pcdescuento,0),IFNULL(pa.preciooferta,0), p.precioconigv, p.igv,
						IFNULL(pa.stockminimo,0), IFNULL(pa.stockreposicion,0), IFNULL(pa.stockmaximo,0),p.maxPorcDescto
		FROM 		producto p 	INNER JOIN unidadmedida u ON p.codUnidadMedida = u.codUnidadMedida
												LEFT OUTER JOIN productoalmacen pa ON p.codProducto = pa.codProducto AND pa.codAlmacen = codalm
												LEFT OUTER JOIN detallelistaprecio dl ON p.codProducto = dl.codProducto AND dl.codListaPrecio = lista
		WHERE 	p.referencia = codpro and pa.stockdisponible >0;
	END IF;


END IF;
END
;;
DELIMITER ;

