DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `MuestraProductoDetalleR`(refe varchar(13), codalm int(11),caso int(11), lista int(11))
BEGIN
DECLARE cont int;
DECLARE v_de INT DEFAULT 0;
DECLARE v_tc DECIMAL(10,3) DEFAULT 0;
SELECT decimales INTO v_de FROM listaprecio WHERE codListaPrecio = lista;
SELECT compra INTO v_tc FROM tipocambio WHERE DATE(fecha) = DATE(NOW()) AND estado = 1;

IF caso = 1 THEN

	SELECT 	p.codProducto, p.referencia, p.descripcion,IFNULL(pa.stockdisponible,0) as stockdisponible, 
					u.codUnidadMedida, u.descripcion, p.codControlStock, p.codsunat, p.tipoimpuesto, p.maxPorcDescto

	FROM 		producto p INNER JOIN unidadmedida u ON p.codUnidadMedida = u.codUnidadMedida
	LEFT OUTER JOIN productoalmacen pa ON p.codProducto = pa.codProducto AND pa.codAlmacen = codalm
	WHERE 	p.referencia = refe;

ELSEIF caso = 2 THEN

	IF lista = 0 THEN

		SELECT 	p.codProducto, p.referencia, p.descripcion,IFNULL(pa.stockdisponible,0) as stockdisponible, u.codUnidadMedida, u.descripcion, p.codControlStock,
						ROUND(IFNULL(p.preciocatalogo,0),v_de) as preciodolares,
						ROUND(IFNULL(p.preciocatalogo * v_tc,0),v_de) as preciosoles,
						IFNULL(pa.preciovariable,1),IFNULL(pa.oferta,0),IFNULL(pa.pcdescuento,0),IFNULL(pa.preciooferta,0), p.codsunat, p.tipoimpuesto,
						IFNULL(pa.stockminimo,0), IFNULL(pa.stockreposicion,0), IFNULL(pa.stockmaximo,0), p.maxPorcDescto
		FROM 		producto p INNER JOIN unidadmedida u ON p.codUnidadMedida = u.codUnidadMedida
		LEFT OUTER JOIN productoalmacen pa ON p.codProducto = pa.codProducto AND pa.codAlmacen = codalm
		WHERE 	p.referencia = refe;

	ELSE
		
    SET cont=(SELECT COUNT(*)
		FROM 		producto p 	INNER JOIN unidadmedida u ON p.codUnidadMedida = u.codUnidadMedida
												LEFT OUTER JOIN productoalmacen pa ON p.codProducto = pa.codProducto AND pa.codAlmacen = codalm
												LEFT OUTER JOIN detallelistaprecio dl ON p.codProducto = dl.codProducto
		WHERE 	p.referencia = refe AND dl.codListaPrecio = lista);

	IF cont = 0 THEN
			SELECT 	p.codProducto, p.referencia, p.descripcion, IFNULL(pa.stockdisponible,0) as stockdisponible, 
									u.codUnidadMedida, u.descripcion, p.codControlStock,  
									ROUND(IFNULL(p.preciocatalogo,0),v_de) as preciodolares,
									ROUND(IFNULL(p.preciocatalogo * v_tc,0),v_de) as preciosoles,
									IFNULL(pa.preciovariable,1),IFNULL(pa.oferta,0),IFNULL(pa.pcdescuento,0),IFNULL(pa.preciooferta,0), 
									p.codsunat, p.tipoimpuesto, IFNULL(pa.stockminimo,0), IFNULL(pa.stockreposicion,0), IFNULL(pa.stockmaximo,0),
									p.maxPorcDescto

					FROM 		producto p INNER JOIN unidadmedida u ON p.codUnidadMedida = u.codUnidadMedida
					LEFT OUTER JOIN productoalmacen pa ON p.codProducto = pa.codProducto AND pa.codAlmacen = codalm
					WHERE 	p.referencia = refe;

	ELSE
			SELECT 	p.codProducto, p.referencia, p.descripcion,IFNULL(pa.stockdisponible,0) as stockdisponible, 
							u.codUnidadMedida, u.descripcion, p.codControlStock, 
							ROUND(IFNULL(IF(dl.precio=0,p.preciocatalogo,dl.precio),p.preciocatalogo),v_de) as preciodolares,
							ROUND(obtienepreciosoles_lista(v_de,IFNULL(if(dl.precio=0,p.preciocatalogo,dl.precio),IFNULL(p.preciocatalogo,0)) * v_tc),v_de) as preciosoles,
							IFNULL(pa.preciovariable,1),IFNULL(pa.oferta,0),IFNULL(pa.pcdescuento,0),IFNULL(pa.preciooferta,0), p.codsunat, p.tipoimpuesto,
							IFNULL(pa.stockminimo,0), IFNULL(pa.stockreposicion,0), IFNULL(pa.stockmaximo,0),p.maxPorcDescto

			FROM 		producto p 	INNER JOIN unidadmedida u ON p.codUnidadMedida = u.codUnidadMedida
													LEFT OUTER JOIN productoalmacen pa ON p.codProducto = pa.codProducto AND pa.codAlmacen = codalm
													LEFT OUTER JOIN detallelistaprecio dl ON p.codProducto = dl.codProducto AND dl.codListaPrecio = lista
			WHERE 	p.referencia = refe;
			END IF;
		
	END IF;
END IF;
END
;;
DELIMITER ;

