DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `MuestraSalidaAlmacen`(codalma int(11), tipo int(1))
BEGIN

IF tipo = 1 THEN 

		SELECT DISTINCT nt.codNotaSalida, DATE_FORMAT(nt.fechasalida,'%d-%m-%Y')as fechasalida, td.sigla, 
					 SUBSTRING(nt.numdocumento,1,3)AS serie, SUBSTRING(nt.numdocumento,5,5)as numdoc, 
					 (SELECT nombre FROM almacen WHERE codAlmacen=codalma)as nomalmacen		
		FROM notasalida nt INNER JOIN detallenotasalida dt ON nt.codNotaSalida=dt.codNotaSalida
				 INNER JOIN tipodocumento td ON td.codTipoDocumento=nt.codTipoDocumento
         LEFT OUTER JOIN factura_venta f ON f.codFacturaV=nt.documentoreferencia
		WHERE dt.cantidad <> (SELECT cantidad FROM detallefactura_venta WHERE codFacturaV=f.codFacturaV and codProducto=dt.codProducto 
                          AND codAlmacen=codalma) 
          AND nt.codAlmacen = codalma 
          AND f.serie = (SELECT s.serie FROM serie s WHERE s.preimpreso=0 AND s.codSerie = f.codSerie)
          AND dt.codProducto = (SELECT pa.codProducto FROM productoalmacen pa WHERE pa.codAlmacen=codalma 
                                AND pa.codProducto= dt.codProducto AND pa.stockporentregar >0)
          AND td.codTipoDocumento=1 OR td.codTipoDocumento=2 and nt.codTransaccion = 7
		GROUP BY dt.codNotaSalida; 

ELSE 

		SELECT DISTINCT nt.codNotaSalida, DATE_FORMAT(nt.fechasalida,'%d-%m-%Y')as fechasalida, td.sigla, 
					 SUBSTRING(nt.numdocumento,1,3)AS serie, SUBSTRING(nt.numdocumento,5,5)as numdoc, 
					 (SELECT nombre FROM almacen WHERE codAlmacen=codalma)as nomalmacen		
		FROM notasalida nt INNER JOIN detallenotasalida dt ON nt.codNotaSalida=dt.codNotaSalida
				 INNER JOIN tipodocumento td ON td.codTipoDocumento=nt.codTipoDocumento
         LEFT OUTER JOIN factura_venta f ON f.codFacturaV=nt.documentoreferencia
		WHERE dt.cantidad <> (SELECT cantidad FROM detallefactura_venta WHERE codFacturaV=f.codFacturaV and codProducto=dt.codProducto 
                          AND codAlmacen=codalma) 
          AND nt.codAlmacen = codalma  AND f.serie = (SELECT s.serie FROM serie s WHERE s.preimpreso=1 AND s.codSerie = f.codSerie)
          AND dt.codProducto = (SELECT pa.codProducto FROM productoalmacen pa WHERE pa.codAlmacen=dt.codAlmacen 
                                AND pa.codProducto= dt.codProducto AND pa.stockporentregar >0)
          AND td.codTipoDocumento=1 OR td.codTipoDocumento=2 and nt.codTransaccion = 7
		GROUP BY dt.codNotaSalida; 
END IF;
END
;;
DELIMITER ;

