-- ----------------------------
-- Procedure structure for MuestraTipoDocumentoOrden
-- ----------------------------
DROP PROCEDURE IF EXISTS `MuestraTipoDocumentoOrden`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `MuestraTipoDocumentoOrden`(codcliente int(11), codalma int(11))
BEGIN
SELECT DISTINCT (fv.codTipoDocumento) as codDocumento, td.descripcion as NDocumento
FROM factura_venta fv INNER JOIN cliente cli ON fv.codCliente=cli.codCliente 
     INNER JOIN tipodocumento td ON fv.codTipoDocumento=td.codTipoDocumento 
     INNER JOIN formapago fp ON fv.formapago = fp.codFormaPago
     INNER JOIN moneda m ON m.codMoneda = fv.moneda
     INNER JOIN detallefactura_venta dfv ON fv.codFacturaV = dfv.codFacturaV
     INNER JOIN producto prod ON dfv.codProducto=prod.codProducto
WHERE fv.codAlmacen=codalma AND fv.estado=1 AND fv.codTransaccion=7 AND dfv.cantidadpendiente > 0 AND 
      cli.codCliente = codcliente
UNION
SELECT DISTINCT (ct.codTipoDocumento) as codDocumento, td.descripcion as NDocumento
FROM cotizacion ct INNER JOIN cliente cli ON ct.codCliente=cli.codCliente
     INNER JOIN tipodocumento td ON ct.codTipoDocumento=td.codTipoDocumento 
     INNER JOIN formapago fp ON ct.formapago = fp.codFormaPago
     INNER JOIN moneda m ON m.codMoneda = ct.moneda
     INNER JOIN detallecotizacion dct ON ct.codCotizacion=dct.codCotizacion
     INNER JOIN producto prod ON dct.codProducto=prod.codProducto
WHERE ct.codAlmacen=codalma AND ct.estado=1 AND dct.cantidadpendiente > 0 AND ct.aprobado=1 AND cli.codCliente = codcliente;
END
;;
DELIMITER ;

