            #IFNULL(pa.precioventa,0) as precioventa, IFNULL(pa.preciooferta,0) as preciooferta
		FROM 		producto p LEFT OUTER JOIN productoalmacen pa ON p.codProducto = pa.codProducto AND pa.codAlmacen = codalma
		WHERE 	p.estado = 1
		AND 		p.codTipoArticulo = tipo
		
		ORDER BY p.referencia;

ELSE

		SELECT p.codProducto, p.referencia, p.descripcion , IFNULL(pa.stockdisponible,0) AS stockdisponible
						, ROUND(IFNULL(if(dl.precio=0,p.preciocatalogo,dl.precio),p.preciocatalogo),v_de) as preciodolares
						,ROUND(obtienepreciosoles_lista(v_de,IFNULL(if(dl.precio=0,p.preciocatalogo,dl.precio),IFNULL(p.preciocatalogo,0)) * v_tc),v_de) as preciosoles
						-- , ROUND((IFNULL(dl.precio,IFNULL(p.preciocatalogo,0)) * v_tc ),3) as preciosolesreal
		FROM 		producto p 	LEFT OUTER JOIN productoalmacen pa ON p.codProducto = pa.codProducto AND pa.codAlmacen = codalma
												LEFT OUTER JOIN detallelistaprecio dl ON dl.codProducto = pa.codProducto AND dl.codListaPrecio = codlista
		WHERE 	p.estado = 1
		AND 		p.codTipoArticulo = tipo		
		
		ORDER BY p.referencia;
END IF;*/

		SELECT 	p.codProducto,p.referencia, p.descripcion , IFNULL(pa.stockdisponible,0) AS stockdisponible, ma.descripcion AS nmarca
						, ROUND((IFNULL((SELECT IFNULL(uq.Precio,0) 
							 FROM unidadequivalente uq 
							 WHERE uq.codProducto = p.codProducto 
							 AND uq.codUnidadMedida = p.codUnidadMedida
							 AND uq.codTipo = 10 -- precio al publico
							 AND uq.compra_venta = 1),0) / v_tc),4) as preciodolares
						, ROUND(IFNULL((SELECT IFNULL(uq.Precio,0) 
							 FROM unidadequivalente uq 
							 WHERE uq.codProducto = p.codProducto 
							 AND uq.codUnidadMedida = p.codUnidadMedida
							 AND uq.codTipo = 10   -- precio al publico
							 AND uq.compra_venta = 1),0),4) as preciosoles,
							grp.descripcion AS modelo
						
		FROM 		producto p 
		INNER JOIN productoalmacen pa ON p.codProducto = pa.codProducto AND pa.codAlmacen = codalma
		INNER JOIN marca ma ON ma.codMarca = p.codMarca
		LEFT JOIN grupo grp ON grp.codGrupo = p.codGrupo
		WHERE 	p.estado = 1
		AND 		p.codTipoArticulo = tipo
		
		ORDER BY p.referencia;

END
;;
DELIMITER ;

