						,ROUND(obtienepreciosoles_lista(v_de,IFNULL(if(dl.precio=0,p.preciocatalogo,dl.precio),IFNULL(p.preciocatalogo,0)) * v_tc),v_de) as preciosoles,
						 grp.descripcion AS modelo
						
