		FROM 		producto p 	LEFT OUTER JOIN productoalmacen pa ON p.codProducto = pa.codProducto
												LEFT OUTER JOIN detallelistaprecio dl ON dl.codProducto = p.codProducto
		LEFT JOIN marca mr ON p.codMarca = mr.codMarca
		LEFT JOIN grupo grp ON p.codGrupo = grp.codGrupo 
		WHERE 	p.estado = 1
		AND 		p.codTipoArticulo = tipo
		AND 		pa.codAlmacen = codalma
		-- AND 		pa.stockdisponible > 0
		AND			dl.codListaPrecio = codlista
		ORDER BY p.referencia;

