CREATE DEFINER=`root`@`localhost` PROCEDURE `ReporteCierreCaja`(codSucur int(11), fecha1 date, fecha2 date)
BEGIN
	#Routine body goes here...
(SELECT su.codSucursal,su.nombre,su.ubicacion,su.telefono,CONCAT("C",LPAD(c.codMovCaja,7,0)) codPersonalizado, c.concepto, 	c.fecha, c.NumDocumento, IFNULL(t.codTipoPagoCaja,0) 
as codTipoPagoCaja, IF(t.descripcion is NULL or TRIM(t.descripcion)='',"EFECTIVO", 
CONCAT(t.descripcion," - EFECTIVO" )) as TipoPagoCaja, c.monto, tipo
FROM cajadiariamovimiento as c LEFT JOIN tipopagocaja as t ON t.codTipoPagoCaja = c.codTipoPagoCaja
INNER JOIN sucursal su on c.codSucursal= su.codSucursal
