DATE(c.fechapago) AS fecha, CONCAT(fv.serie,'-',fv.numDocumento) AS NumDocumento, "Efectivo" as codTipoPagoCaja,
'INGRESO' as TipoMovimiento, c.montocobrado AS monto, 
3 AS tipo
FROM pago as c INNER JOIN almacen al ON c.codAlmacen=al.codAlmacen
INNER JOIN sucursal su ON al.codSucursal=su.codSucursal
INNER JOIN factura_venta fv ON c.codNota=fv.codFacturaV
INNER JOIN tipodocumento td ON fv.codTipoDocumento= td.codTipoDocumento
WHERE su.codSucursal = codSucur AND DATE(c.fecharegistro) BETWEEN DATE(fecha1 ) and DATE (fecha2 ) 
