DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ReporteCobrosDia`(vend int(11), fecha1 datetime, fecha2 datetime, codalma int(11), moned int(11))
BEGIN

IF vend = 0 THEN

  SELECT IFNULL(v.codVendedor,0)AS codVendedor, IFNULL(v.dni,"")AS dni_vend, IFNULL(v.nombre,"")AS nom_vend, 
         ns.codFacturaV, ns.codTipoDocumento, t.sigla, s.codSerie, CONCAT(s.serie,'-',ns.numdocumento)AS documento,
         c.codCliente, c.codigopersonalizado, c.razonsocial, DATEDIFF(ns.fechapago,NOW())AS mora, ns.total, 
         ns.pendiente, p.montocobrado, DATE_FORMAT(fecha1,'%d-%m-%Y')AS fecha1, IFNULL(c.ruc,c.dni)AS ruc_dni,
         DATE_FORMAT(fecha2,'%d-%m-%Y')AS fecha2, DATE_FORMAT(p.fechapago,'%d-%m-%Y')AS fechapago, ns.abonado, 
				IF(p.codMoneda=1,'SOLES', 'DOLARES') AS moneda
	FROM pago p INNER JOIN factura_venta ns ON ns.codFacturaV = p.codNota 
       LEFT JOIN cliente c ON ns.codCliente = c.codCliente 
       LEFT JOIN vendedor v ON v.codVendedor = c.codVendedor 
       LEFT JOIN serie s ON s.codSerie = ns.codSerie 
       LEFT JOIN tipodocumento t ON t.codTipodocumento = ns.codTipoDocumento
	WHERE p.ingresoegreso = 1 AND ns.anulado = 0 AND ns.estado = 1 AND p.estado = 1 AND p.codMoneda = moned AND
        DATE(p.fechapago) BETWEEN DATE(fecha1) AND DATE(fecha2) AND ns.codAlmacen=codalma;

