		(SELECT IFNULL(SUM(n.total),0) FROM factura_venta n LEFT JOIN formapago f ON f.codFormaPago = n.formapago WHERE f.tipo = 1 AND n.anulado = 0 AND n.estado = 1 AND n.moneda = 2 AND DATE(n.fechasalida) BETWEEN DATE(fecha1) AND DATE(fecha2)) AS contadoDolares,
