-- ----------------------------
-- Procedure structure for ReporteDeTransferencia
-- ----------------------------
DROP PROCEDURE IF EXISTS `ReporteDeTransferencia`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ReporteDeTransferencia`(fecha1 date, fecha2 date, codalma int(11))
BEGIN
	SELECT CONCAT(traf.serie,"-",traf.numerodoc) as documento , alm.nombre AS origen, alm2.nombre AS destino, prot.referencia, 
				 prot.descripcion as descripcion, dtra.cantidad,dtra.fecharegistro , unm.sigla as unidad,
				(SELECT CASE traf.pendiente WHEN 0 THEN "ACEPTADO" WHEN 1 THEN "PENDIENTE" END) AS pendient
	FROM transferencia traf
			INNER JOIN detalletransferencia dtra ON traf.codTransDir = dtra.codTransDir
			INNER JOIN producto prot ON dtra.codProducto = prot.codProducto
			LEFT JOIN almacen alm ON alm.codAlmacen = traf.codAlmacenOrigen
			LEFT JOIN almacen alm2 ON alm2.codAlmacen = traf.codAlmacenDestino
			LEFT JOIN unidadmedida unm ON unm.codUnidadMedida = prot.codUnidadMedida
	WHERE traf.fechaenvio BETWEEN fecha1 AND fecha2 AND traf.codAlmacenOrigen=codalma
	/*GROUP BY documento, origen, destino*/
	UNION
	
	SELECT CONCAT(nt.serie,"-",nt.numdocumento) as documento , alm.nombre AS origen, alm2.nombre AS destino, prot.referencia, 
				 prot.descripcion as descripcion, dtn.cantidad,dtn.fecharegistro , unm.sigla as unidad,
				(SELECT CASE tr.pendiente WHEN 0 THEN "APROBADO" WHEN 1 THEN "PENDIENTE" END) AS pendient
	FROM notaingreso nt
			INNER JOIN detallenotaingreso dtn ON nt.codNotaIngreso = dtn.codNotaIngreso
			INNER JOIN producto prot ON dtn.codProducto = prot.codProducto
			INNER JOIN transferencia tr ON tr.codTransDir = nt.codTransferencia
			LEFT JOIN almacen alm2 ON alm2.codAlmacen = tr.codAlmacenDestino
			LEFT JOIN almacen alm ON alm.codAlmacen = tr.codAlmacenOrigen			
			LEFT JOIN unidadmedida unm ON unm.codUnidadMedida = prot.codUnidadMedida
	WHERE nt.fechaingreso BETWEEN fecha1 AND fecha2 AND nt.codAlmacen = codalma;
END
;;
DELIMITER ;

