BEGIN

DECLARE grecia BLOB;
	
				SELECT 	dp.codProducto AS det_codProducto,
								p.referencia AS det_referencia,
								IF(pv.codTransaccion = 5,CONCAT(p.descripcion, " - ", "PAGO POR ANTICIPO"), p.descripcion) AS det_descripcionPrd,
