								dp.valorpromedio AS det_valorpromedio,
                
								c.codCliente AS cli_codCliente,
								c.codigopersonalizado AS cli_codigopersonalizado,
								IF(pv.codTipoDocumento=1,c.nombre, c.razonsocial)as cli_nombrerazon, 
								IF(pv.codTipoDocumento=1,c.dni, c.ruc)as cli_dniruc,
								pv.codTipoDocumento,
								c.direccionlegal AS cli_direccionlegal,
								c.comentario AS cli_comentario,
								#z.codZona AS cli_codZona,
								#z.descripcion as cli_Zona, 

								c.codVendedor AS ven_codVendedor, 
								v.nombre AS ven_vendedores, 
								pv.formapago  AS ven_formapago, 

								fp.descripcion AS venta_condicion, 
								pv.fechasalida AS venta_Emision, 
								(pv.total-pv.igv) as venta_bruto, 
								pv.igv AS venta_igv, 
								pv.total AS venta_total, 
								pv.codUsuario AS venta_codUsuario,
								pv.fechapago AS venta_fecharegistro,
								s.serie AS venta_serie, 
								mon.descripcion AS venta_moned,
								IF(pv.tipoventa,"G","E")AS venta_tipo,
								(CASE pv.codTipoDocumento 
								WHEN 1 THEN CONCAT("B",pv.serie," - ", LPAD(pv.numDocumento,8,0) )
								WHEN 2 THEN CONCAT("F",pv.serie," - ", LPAD(pv.numDocumento,8,0) )
								WHEN 7 THEN CONCAT("TK",pv.serie," - ", LPAD(pv.numDocumento,8,0) )
								END) as venta_numDocumento,
								pv.gravadas AS venta_gravadas,
								pv.exoneradas AS venta_exoneradas,
								pv.gratuitas AS venta_gratuitasm,
								pv.inafectas AS venta_inafectas,
								(CASE pv.codTipoDocumento
								WHEN 1 THEN "BOLETA ELECTRONICA"
								WHEN 2 THEN "FACTURA ELECTRONICA"
								WHEN 7 THEN "TICKET DE VENTA"
								END) AS nombredocumento,

								emp.razonsocial AS empresa_razonsocial,
								emp.direccion AS empresa_direccion, 
								emp.ruc AS empresa_ruc, 

								suc.descripcion as sucursal_descripcion,
								suc.telefono as sucursal_telefono,
								suc.ubicacion as sucursal_ubicacion,
								pv.codFacturaV, 
								CONCAT(usu.nombre," ",usu.apellido)as cajero,
								pv.montodscto, 
								'' AS detallecomentario,
								grecia AS firma,
								IF(pv.codPedido = 0, "00000000",LPAD(pv.codPedido,8,0)) AS pedido,
								pv.codigobarras,
								pv.codigoBarrasCifrado,
								pv.MontoAnticipo,
								#pv.efectivo,
								#pv.vuelto,
								(CASE pv.codTransaccion 
								WHEN 5 THEN "VENTA INTERNA - ANTICIPOS"
								WHEN 7 THEN "VENTA INTERNA"
								END) as operacion,
								IFNULL(PV.DocumentoReferenciaAnticipo, "") AS DocumentoReferenciaAnticipo,
								(pv.gravadas + pv.exoneradas + pv.gratuitas + pv.inafectas) AS venta_subtotal,
								emp.telefono
								#emp.logo
        
				FROM detallefactura_venta dp LEFT JOIN producto p ON p.codProducto = dp.codProducto
						 LEFT JOIN unidadmedida u ON p.codUnidadMedida = u.codUnidadMedida 
						 LEFT JOIN factura_venta pv ON pv.codFacturaV = dp.codFacturaV 
						 LEFT JOIN cliente c ON c.codCliente = pv.codCliente
						 LEFT JOIN vendedor v ON v.codVendedor = pv.codVendedor 
						 LEFT JOIN formapago fp ON fp.codFormaPago = pv.formapago 
