						 LEFT JOIN serie s ON s.codSerie = pv.codSerie 
						 LEFT JOIN almacen al ON al.codAlmacen = dp.codAlmacen 
						 LEFT JOIN empresa emp ON emp.codEmpresa = al.codEmpresa
						 LEFT JOIN moneda mon on mon.codMoneda=pv.moneda
						 LEFT JOIN sucursal suc ON suc.codSucursal = al.codSucursal
						 LEFT JOIN usuario usu ON usu.codUsuario = dp.codUser
				WHERE pv.codFacturaV = cod AND pv.estado = 1;


END
;;
