				AND DATE(dnis.fechaingreso)<fecha1),0) AS cosIni*/

(IFNULL((SELECT IF(di.moneda=1,(IFNULL(SUM(di.cantidad*di.valoreal),0)), (IFNULL(SUM(di.cantidad*di.valoreal*nin.tipocambio),0))) 
				 FROM detallenotaingreso di 
				 INNER JOIN notaingreso nin ON nin.codNotaIngreso = di.codNotaIngreso				 
				 WHERE nin.anulado = 0 
         AND di.codProducto = p.codProducto 
				 AND nin.codAlmacen=codalma
				 AND (DATE(nin.fechaingreso) < fecha1)),0) -
				 IFNULL((SELECT IF(nsa.moneda=1,(IFNULL(SUM(ds.cantidad*ds.valorpromediosoles),0)), (IFNULL(SUM(ds.cantidad*ds.valorpromediosoles*nsa.tipocambio),0))) 
         FROM detallenotasalida ds 
         INNER JOIN notasalida nsa ON nsa.codNotaSalida = ds.codNotaSalida         
				 WHERE nsa.anulado = 0 
				 AND nsa.estado = 1 
				 AND ds.codProducto = p.codProducto 
				 AND ds.codAlmacen=codalma
				 AND (DATE(nsa.fechasalida) < fecha1)),0)) AS cosIni

				FROM detallenotasalida dns INNER JOIN notasalida ns ON ns.codNotaSalida = dns.codNotaSalida
				INNER JOIN transaccion tr ON tr.codTransaccion = ns.codTransaccion
				LEFT OUTER JOIN cliente cl ON cl.codCliente = ns.codCliente
				INNER JOIN tipodocumento td ON td.codTipoDocumento = ns.codTipoDocumento
				LEFT OUTER JOIN serie s ON s.codSerie = ns.codSerie
				INNER JOIN producto p ON p.codProducto = dns.codProducto
				WHERE ns.codAlmacen = codalma AND dns.codProducto = codPro AND ns.estado = 1 AND ns.anulado = 0 AND date(ns.fechasalida) BETWEEN fecha1 AND fecha2
				ORDER BY 	fecha,tipo;

END IF;
END
;;
