CREATE DEFINER=`root`@`localhost` PROCEDURE `ReporteKardex_TRABAJAR`(fecha1 date, fecha2 date,codPro int(11), codalma int(11))
BEGIN
DECLARE nomalm VARCHAR (255) DEFAULT '';
DECLARE codUnidad INT(11) DEFAULT 0;
DECLARE Ingresos, Salidas DECIMAL(20,4) DEFAULT 0;
DECLARE stockIng, stockSal DECIMAL(20,4) DEFAULT 0;
DECLARE costoIng, costoSal DECIMAL(20,4) DEFAULT 0;
DECLARE tipoexistencia VARCHAR(255) DEFAULT '';

SELECT IFNULL(nombre,'') INTO nomalm FROM almacen WHERE codAlmacen = codalma;
SELECT IFNULL(Unidad,0) INTO codUnidad FROM productoalmacen WHERE codProducto = codPro AND codAlmacen = codalma;
SELECT CONCAT("(01)","     ",IFNULL(descripcion,'')) INTO tipoexistencia FROM tipoarticulo WHERE codtipoarticulo = 1;
-- Ingresos - costoIng
SELECT
IFNULL(SUM((SELECT IFNULL(factor,0) 
FROM unidadequivalente eq 
WHERE eq.codProducto = codPro AND 
eq.codUnidadMedida = di.unidadingresada AND 
eq.codUndEqui = codUnidad AND 
eq.compra_venta = 2)*di.cantidad), 0), 
IF(di.moneda = 0,(IFNULL(SUM(di.cantidad*di.valoreal),0)), (IFNULL(SUM(di.cantidad*di.valoreal*nin.tipocambio),0))) 
INTO Ingresos, costoIng
FROM detallenotaingreso di INNER JOIN notaingreso nin ON nin.codNotaIngreso = di.codNotaIngreso
WHERE nin.anulado = 0 AND 
nin.estado = 1 AND
di.estado = 1 AND
di.codProducto = codPro AND 
DATE(nin.fechaingreso) < DATE(fecha1) AND 
nin.codAlmacen = codalma;
-- Salidas - costoSal
SELECT
IFNULL(SUM(eq.factor*dsx.cantidad), 0),  
IF(nsnx.moneda = 0,(IFNULL(SUM(eq.factor*dsx.cantidad*dsx.valorpromedio),0)), (IFNULL(SUM(eq.factor*dsx.cantidad*dsx.valorpromedio*nsnx.tipocambio),0)))
INTO Salidas, costoSal 
FROM detallenotasalida dsx INNER JOIN notasalida nsnx ON nsnx.codNotaSalida = dsx.codNotaSalida
INNER JOIN productoalmacen pa ON pa.codProducto = dsx.codProducto AND pa.codAlmacen = 1 
INNER JOIN unidadequivalente eq ON eq.codProducto = dsx.codProducto AND eq.codUnidadMedida = dsx.unidadingresada AND eq.codUndEqui = pa.Unidad AND eq.compra_venta = 2
WHERE nsnx.anulado = 0 AND 
dsx.codProducto = codPro AND
pa.codProducto = codPro AND
nsnx.estado = 1 AND
dsx.estado = 1 AND
DATE(nsnx.fechasalida) < DATE(fecha1) AND 
nsnx.codAlmacen = codalma;
-- INTO stockIng
SELECT
IFNULL(SUM((SELECT IFNULL(factor,0) 
FROM unidadequivalente eq 
WHERE eq.codProducto = codPro AND 
eq.codUnidadMedida = di.unidadingresada AND 
eq.codUndEqui = codUnidad AND 
eq.compra_venta = 2)*di.cantidad), 0) INTO stockIng
FROM detallenotaingreso di INNER JOIN notaingreso nin ON nin.codNotaIngreso = di.codNotaIngreso
WHERE nin.anulado = 0 AND 
nin.estado = 1 AND
di.estado = 1 AND
di.codProducto = codPro AND 
DATE(nin.fechaingreso) <= DATE(fecha2) AND 
nin.codAlmacen = codalma;
-- INTO stockSal
SELECT
IFNULL(SUM((SELECT IFNULL(factor,0) 
FROM unidadequivalente eq 
WHERE eq.codProducto = codPro AND 
eq.codUnidadMedida = ds.unidadingresada AND 
eq.codUndEqui = codUnidad AND 
eq.compra_venta = 2)*ds.cantidad),0) INTO stockSal
FROM detallenotasalida ds INNER JOIN notasalida nsn ON nsn.codNotaSalida = ds.codNotaSalida
WHERE nsn.anulado = 0 AND 
nsn.estado = 1 AND
ds.estado = 1 AND
ds.codProducto = codPro AND 
DATE(nsn.fechasalida) <= DATE(fecha2) AND 
nsn.codAlmacen = codalma;

IF codPro = 0 THEN
				
(SELECT	
	dni.codDetalleIngreso AS codDetalle,
	nomalm,
	p.codFamilia,
	(SELECT f.descripcion from familia f INNER JOIN producto pr ON pr.codFamilia = f.codFamilia WHERE pr.codProducto = dni.codProducto) AS fam,
	p.codLinea,
	(SELECT l.descripcion from linea l INNER JOIN producto pr ON pr.codLinea = l.codLinea WHERE pr.codProducto = dni.codProducto) AS lin,
	p.codProducto,
	p.referencia,
	p.descripcion,
	"NI" AS tipo,
	ni.codNotaIngreso AS codNota, 
	DATE(ni.fechaingreso) AS fecha,
	LPAD((SELECT SUBSTRING_INDEX(ni.numdocumento,'-',1)),4,0) AS serie,
