	(eq.factor*dni.cantidad) AS cant_i,
	dni.valoreal AS c_i,
	(dni.cantidad * dni.valoreal) AS t_i,
	0 AS cant_s,
	0 AS c_s,
	0 AS t_s,
	DATE(fecha1) AS fecha1,
	DATE(fecha2) AS fecha2,
	(IFNULL(t1.Ingresos,0) - IFNULL(t2.Salidas,0)) AS saldo_inicial,
	(IFNULL(t1.costoIng,0) - IFNULL(t2.costoSal,0)) AS cosIni,
	IFNULL(t3.stockactual,0) AS stockactual,
	CONCAT('(',IFNULL(u.codsunat,'99'),')     ',IFNULL(u.descripcion,'')) AS unidad,
	IFNULL(td.codsunat,'99') AS tdsunat,
	IFNULL(tr.codsunat,'99') AS tipooperacion,
	e.razonsocial as empresa,
	e.ruc,
	nomalm AS establecimiento,
	tipoexistencia
		
FROM detallenotaingreso dni INNER JOIN notaingreso ni ON ni.codNotaIngreso = dni.codNotaIngreso
INNER JOIN transaccion tr ON tr.codTransaccion = ni.codTransaccion
INNER JOIN unidadmedida u ON u.codunidadmedida = dni.unidadingresada
INNER JOIN productoalmacen pa ON pa.codProducto = dni.codProducto AND pa.codAlmacen = codalma
INNER JOIN almacen a ON a.codAlmacen = ni.codAlmacen
INNER JOIN empresa e ON e.codEmpresa = a.codEmpresa
INNER JOIN tipodocumento td ON td.codTipoDocumento = ni.codTipoDocumento
INNER JOIN unidadequivalente eq ON eq.codProducto = dni.codProducto AND eq.compra_venta = 2 AND eq.codUndEqui = pa.Unidad AND eq.codUnidadMedida = dni.unidadingresada
INNER JOIN producto p ON p.codProducto = dni.codProducto
/* -------------- Ingresos - costoIng ----------------- */

LEFT JOIN 
(SELECT
IFNULL(SUM(eq.factor*dix.cantidad), 0) AS Ingresos,  
IF(dix.moneda = 0,(IFNULL(SUM(dix.cantidad*dix.valoreal),0)), (IFNULL(SUM(dix.cantidad*dix.valoreal*ninx.tipocambio),0))) AS costoIng,
dix.codProducto 
FROM detallenotaingreso dix INNER JOIN notaingreso ninx ON ninx.codNotaIngreso = dix.codNotaIngreso
INNER JOIN productoalmacen pa ON pa.codProducto = dix.codProducto AND pa.codAlmacen = codalma 
INNER JOIN unidadequivalente eq ON eq.codProducto = dix.codProducto AND eq.codUnidadMedida = dix.unidadingresada AND eq.codUndEqui = pa.Unidad AND eq.compra_venta = 2
WHERE ninx.anulado = 0 AND 
ninx.estado = 1 AND
dix.estado = 1 AND
DATE(ninx.fechaingreso) < DATE(fecha1) AND 
ninx.codAlmacen = codalma GROUP BY codProducto) t1 ON t1.codProducto = dni.codProducto

/* -------------- Salidas - costoSal ----------------- */

LEFT JOIN
(SELECT
IFNULL(SUM(eq.factor*dsx.cantidad), 0) AS Salidas,  
IF(nsnx.moneda = 0,(IFNULL(SUM(eq.factor*dsx.cantidad*dsx.valorpromedio),0)), (IFNULL(SUM(eq.factor*dsx.cantidad*dsx.valorpromedio*nsnx.tipocambio),0))) AS costoSal,
dsx.codProducto 
