INNER JOIN productoalmacen pa ON pa.codProducto = dsx.codProducto AND pa.codAlmacen = codalma 
INNER JOIN unidadequivalente eq ON eq.codProducto = dsx.codProducto AND eq.codUnidadMedida = dsx.unidadingresada AND eq.codUndEqui = pa.Unidad AND eq.compra_venta = 2
WHERE nsnx.anulado = 0 AND 
nsnx.estado = 1 AND
dsx.estado = 1 AND
DATE(nsnx.fechasalida) < DATE(fecha1) AND 
nsnx.codAlmacen = codalma GROUP BY codProducto) t2 ON t2.codProducto = dni.codProducto 

/* -------------- stockactual ----------------- */

INNER JOIN
(SELECT t1.stockIng - t2.stockSal AS stockactual, t1.codProducto FROM
(SELECT
IFNULL(SUM(eq.factor*dix.cantidad), 0) AS stockIng, 
dix.codProducto 
FROM detallenotaingreso dix INNER JOIN notaingreso ninx ON ninx.codNotaIngreso = dix.codNotaIngreso
INNER JOIN productoalmacen pa ON pa.codProducto = dix.codProducto AND pa.codAlmacen = codalma  
INNER JOIN unidadequivalente eq ON eq.codProducto = dix.codProducto AND eq.codUnidadMedida = dix.unidadingresada AND eq.codUndEqui = pa.Unidad AND eq.compra_venta = 2
WHERE ninx.anulado = 0 AND 
ninx.estado = 1 AND
DATE(ninx.fechaingreso) <= DATE(fecha2) AND 
ninx.codAlmacen = codalma  GROUP BY codProducto) t1 INNER JOIN 
(SELECT
IFNULL(SUM(eq.factor*dsx.cantidad), 0) AS stockSal,  
dsx.codProducto 
FROM detallenotasalida dsx INNER JOIN notasalida nsnx ON nsnx.codNotaSalida = dsx.codNotaSalida
INNER JOIN productoalmacen pa ON pa.codProducto = dsx.codProducto AND pa.codAlmacen = codalma  
INNER JOIN unidadequivalente eq ON eq.codProducto = dsx.codProducto AND eq.codUnidadMedida = dsx.unidadingresada AND eq.codUndEqui = pa.Unidad AND eq.compra_venta = 2
WHERE nsnx.anulado = 0 AND 
nsnx.estado = 1 AND
dsx.estado = 1 AND
DATE(nsnx.fechasalida) <= DATE(fecha2) AND 
nsnx.codAlmacen = codalma  GROUP BY codProducto) t2 ON t1.codProducto = t2.codProducto) t3 ON t3.codProducto = dni.codProducto
/* -----------------------------------------------*/

WHERE ni.codAlmacen = codalma AND
ni.estado = 1 AND 
ni.anulado = 0 AND 
dni.estado = 1 AND
DATE(ni.fechaingreso) BETWEEN DATE(fecha1) AND DATE(fecha2))

UNION

(SELECT	
	dns.codDetalleSalida AS codDetalle,
