	(SELECT f.descripcion from familia f INNER JOIN producto pr ON pr.codFamilia = f.codFamilia WHERE pr.codProducto = dns.codProducto) AS fam,
	p.codLinea,
	(SELECT l.descripcion from linea l INNER JOIN producto pr ON pr.codLinea = l.codLinea WHERE pr.codProducto = dns.codProducto) AS lin,
	p.codProducto,
	p.referencia,
	p.descripcion,
	"NI" AS tipo,
	ns.codNotaSalida AS codNota, 
	DATE(ns.fechasalida) AS fecha,
	(SELECT IFNULL(serie,'') FROM serie WHERE codSerie = ns.codSerie) AS serie,
