DROP PROCEDURE IF EXISTS `ReporteMovimientosCajaVentas`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ReporteMovimientosCajaVentas`(codSucur int(11), fecha1 date, codcaja_ex int(11), codalma_ex int(11))
BEGIN
#Routine body goes here...
 
SELECT 	cm.codMovCaja, 
					cm.codSucursal, 
					cm.codcaja, 
					IFNULL(cm.codPago,0) as codPago,
					-- cm.concepto AS concepto, 					
					IF((IFNULL(cm.codPago,0)=0),cm.concepto,IF(pg.observacion="",cm.concepto,pg.observacion)) as concepto,
					cm.monto, 
					cm.tipo,  # tipo => 2 egreso     1 ingreso
					(CASE cm.tipo WHEN 1 THEN "INGRESO" WHEN 2 THEN "EGRESO" END) AS ingresoegreso,
					cm.tipomovimiento,
					(CASE cm.tipomovimiento WHEN 1 THEN "APERTURA" WHEN 2 THEN "MOVIMIENTO" WHEN 3 THEN "CIERRE" END) AS tipomovimientodescripcion,
					cm.fecha, 
					cm.tipodocumento,
					IFNULL((SELECT sigla FROM tipodocumento WHERE codTipoDocumento = cm.tipodocumento),"") AS documento,
					cm.codSerie, 
					cm.serie, 
					CONCAT(IFNULL((SELECT sigla FROM tipodocumento WHERE codTipoDocumento = cm.tipodocumento),"")," ",cm.serie, " - ",LPAD(cm.NumDocumento,9,0)) AS NumDocumento,
					(CASE cm.tipo
					WHEN 1 THEN IFNULL((SELECT CONCAT((SELECT t.sigla FROM tipodocumento t WHERE t.codTipoDocumento = ns.codTipoDocumento), " ", ns.serie, " - ", LPAD(ns.numdocumento,7,0)) 
											FROM pago p 
											INNER JOIN factura_venta ns on ns.codFacturaV = p.codNota 
											WHERE p.codPago = cm.codPago),"")
					WHEN 2 THEN IFNULL((SELECT ni.DocumentoFactura
											FROM pago p 
											INNER JOIN facturacion ni on ni.codFactura = p.codNota 
											WHERE p.codPago = cm.codPago AND p.notacredito != 1),
											(SELECT ni.DocumentoFactura 
											FROM pago p 
											INNER JOIN notacredito ni on ni.codNotaCredito = p.codNota # cambiar
											WHERE p.codPago = cm.codPago AND p.notacredito = 1))
					END) AS documentorefencia, 
					cm.toneladas, 
					cm.codTipoPagoCaja,
					IFNULL((CASE cm.codTipoPagoCaja WHEN 5 THEN "EFECTIVO" WHEN 6 THEN "DEPOSITO" WHEN 7 THEN "CHEQUE" WHEN 8 THEN "TARJETA" END),"") AS PAGOCAJA,
					0 AS SALDO,
					cm.nombre,
					cm.dni,
					cm.estado, 
					cm.codUser, 
					cm.fecharegistro,
					c.codcaja, c.fechaapertura, c.montoapertura, c.montocierre, c.totalIngreso, c.totalEgreso, c.totalDisponible,
					suc.descripcion, suc.ubicacion, suc.nombre, suc.telefono
	FROM 		caja c
	INNER JOIN cajamovimiento cm ON cm.codcaja = c.codcaja
	INNER JOIN sucursal suc ON suc.codSucursal = c.codsucursal
	LEFT JOIN pago pg ON pg.codPago=cm.codPago
	WHERE 	c.codsucursal = codSucur 
	AND 		DATE(c.fechaapertura) = DATE(fecha1)
	AND 		c.codcaja = codcaja_ex AND c.codalmacen = codalma_ex
	AND			cm.estado=1;  
END
;;
DELIMITER ;

