DROP PROCEDURE IF EXISTS `ReporteNIngreso`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ReporteNIngreso`()
BEGIN
	#Routine body goes here...
	SELECT pr.codProducto,
				pr.referencia,
				pr.descripcion,
				(dfv.cantidad) AS Cantidad,
				(dfv.preciounitario) as PreUnitario,
				(dfv.importe) as Total,					
				emp.ruc,al.descripcion, al.ubicacion, al.nombre, al.telefono,fecha1,fecha2,
				fv.DocumentoFactura,
				fv.fecharegistro,
				IFNULL(prv.razonsocial,"Sin Proveedor")as NomProveedor
		FROM notaingreso fv 
		INNER JOIN detallenotaingreso dfv ON fv.codFactura=dfv.codFactura
		INNER JOIN almacen al ON al.codAlmacen=fv.codalmacen 
		LEFT JOIN empresa emp ON emp.codEmpresa=al.codEmpresa
		LEFT JOIN producto pr ON pr.codProducto=dfv.codProducto
		LEFT JOIN tipodocumento td ON td.codTipoDocumento = fv.codTipoDocumento
		LEFT JOIN proveedor prv ON prv.codProveedor=fv.codProveedor
		WHERE fv.estado=1 AND fv.anulado=0 AND fv.codTransaccion=1  -- AND (fv.fechasalida=DATE("2017-09-26"))
		AND fv.codAlmacen=codalma AND fv.fechaingreso BETWEEN DATE(fecha1) AND DATE(fecha2) 
		GROUP BY DocumentoFactura	ORDER BY fv.fecharegistro,pr.referencia;

END
;;
DELIMITER ;

