BEGIN
	#Routine body goes here...
SELECT pl.codAlmacen,dni.codProducto,p.referencia,p.descripcion,p.codUnidadMedida,u.sigla,ABS(dni.cantidad) as cantidad,
dni.preciounitario,dni.importe,c.codCliente,c.codigopersonalizado,c.razonsocial,c.nombre,c.direccionlegal,
IFNULL(c.ruc,'') as ruc,IFNULL(c.dni,'') as dnii,c.codVendedor, v.nombre AS vendedores, ni.formapago, fp.descripcion AS condicion,
DATE(ni.fechaingreso) AS Emision, ni.bruto, ni.igv, ni.total, ni.codUsuario,
ni.fecharegistro,ns.serie,ns.numdocumento,DATE(ns.fechasalida) AS fechasal,
(CONCAT('APLICA EN ',td.sigla,' ',ns.serie,'-',ns.numdocumento,' DEL ',DATE(ns.fechasalida))) AS sello
FROM detallenotaingreso dni LEFT JOIN producto p
