CREATE DEFINER=`root`@`localhost` PROCEDURE `ReportePagos`(empre int(11), fecha1 datetime, fecha2 datetime, esta bit)
BEGIN
SELECT nt.codNotaIngreso,(SELECT CASE esta WHEN 0 THEN "PENDIENTES" WHEN 1 THEN "CANCELADOS" END) AS cabecera, nt.numdocumento,nt.codTipoDocumento,td.sigla,pr.codProveedor, pr.ruc, pr.razonsocial, DATE(nt.fechaingreso), DATE(nt.fechapago),
				(SELECT CASE nt.moneda WHEN 0 THEN "SOLES" WHEN 1 THEN "DOLARES" END) AS moneda ,nt.total, nt.pendiente, pr.banco, pr.ctacte
	FROM notaingreso nt LEFT OUTER JOIN proveedor pr ON nt.codProveedor = pr.codProveedor	
											LEFT OUTER JOIN almacen al ON nt.codAlmacen = al.codAlmacen 
											LEFT OUTER JOIN tipodocumento td ON td.codTipoDocumento = nt.codTipoDocumento
											INNER JOIN transaccion tr ON nt.codTransaccion = tr.codTransaccion AND tr.codTransaccion = 1 ##codigo de IMGRESO POR COMPRA
	WHERE nt.cancelado = esta AND nt.estado = 1 AND al.codEmpresa = empre AND date(nt.fechaingreso) BETWEEN date(fecha1) AND date(fecha2);
END
;;
