DROP PROCEDURE IF EXISTS `ReporteRankingVentasArticulo`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ReporteRankingVentasArticulo`(fecha1 datetime, fecha2 datetime /*, codalma int(11)*/)
BEGIN
	
SELECT dns.codProducto, p.referencia, p.descripcion, SUM(k.cantidad) AS cantidad, 
       FORMAT((SUM(k.cantidad*dns.valoreal)/SUM(k.cantidad)),4) AS p_unit,
       SUM(k.cantidad*dns.valoreal) AS valor_venta, SUM(k.total) AS costo_venta,
       ((SUM(k.cantidad*dns.valoreal)) - SUM(k.total)) AS util,
       FORMAT((((SUM(k.cantidad*dns.valoreal)) - SUM(k.total))/(SUM(k.total)))*100,2) AS pct_util,
       DATE_FORMAT(k.fechatransaccion,"%d-%m-%Y")AS fechatransaccion, DATE_FORMAT(fecha1,"%d-%m-%Y")AS fecha1,
       DATE_FORMAT(fecha2,"%d-%m-%Y")AS fecha2

FROM detallefactura_venta dns LEFT OUTER JOIN producto AS p
ON p.codProducto = dns.codProducto LEFT OUTER JOIN productoalmacen pa
ON pa.codProducto = p.codProducto INNER JOIN kardex k
ON dns.codDetalleFacturaV = k.coddetallesalida

WHERE k.coddetallesalida >=1 AND k.estado = 0 AND DATE(k.fechatransaccion) BETWEEN DATE(fecha1) AND DATE(fecha2) /*AND 
      dns.codAlmacen = codalma*/
GROUP BY dns.codProducto
ORDER BY p.descripcion ASC;
END
;;
DELIMITER ;

