CREATE DEFINER=`root`@`localhost` PROCEDURE `ReporteRankingVentasCliente`(fecha1 date, fecha2 date)
BEGIN
	
SELECT ns.codCliente, c.codigopersonalizado,IF(c.nombre IS NULL OR c.nombre = "",c.razonsocial,c.nombre) AS nombre,
SUM(k.cantidad*dns.valoreal) AS valor_venta, SUM(dns.igv) AS igv,(SUM(k.cantidad*dns.valoreal)+SUM(dns.igv)) AS total_venta,
SUM(k.total) AS costo_venta,(SUM(k.cantidad*dns.valoreal) - SUM(k.total)) AS util,
FORMAT((((SUM(k.cantidad*dns.valoreal)) - SUM(k.total))/(SUM(k.cantidad*dns.valoreal)))*100,2) AS pct_util,
k.fechatransaccion,fecha1,fecha2
FROM factura_venta ns INNER JOIN cliente AS c
