-- ----------------------------
-- Procedure structure for ReporteStatusCaja
-- ----------------------------
DROP PROCEDURE IF EXISTS `ReporteStatusCaja`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ReporteStatusCaja`(fechaini datetime, fechafin datetime, sucur int(11))
BEGIN

/* SOLES  Y  DOLARES */
SELECT IFNULL((SELECT montoApertura FROM flujocaja WHERE  estado = 1 and DATE(fecharegistro) = DATE(fechaini) 
                and codSucursal = sucur),0.000)as AperturaCaja,
       IFNULL((SELECT SUM(monto) FROM caja WHERE tipo = 1 AND estado = 1 and DATE(fecha) BETWEEN DATE(fechaini) and DATE(fechafin) and codSucursal = sucur),0.000)as ingreso,
       IFNULL((SELECT SUM(monto) FROM caja WHERE tipo = 2 AND estado = 1 and DATE(fecha) BETWEEN DATE(fechaini) and DATE(fechafin) and codSucursal = sucur),0.000)as egreso,
       IFNULL((SELECT SUM(fv.abonado) FROM factura_venta fv INNER JOIN formapago fp ON fv.formapago=fp.codFormaPago
               WHERE fv.anulado = 0 and fv.estado = 1 and DATE(fv.fechapago) BETWEEN DATE(fechaini) and DATE(fechafin) and fp.tipoaccion = 1  and fv.formapago=6 
                     and /*fv.formapago=6 and*/ fv.codSucursal=sucur and fv.codTransaccion=7 and fv.moneda = 1),0.000)as TotalVentas,
       IFNULL((SELECT SUM(total) from factura_venta WHERE anulado = 0 and estado = 1 and codTransaccion = 7 
               and DATE(fechasalida) BETWEEN DATE(fechaini) and DATE(fechafin) and moneda = 1 and formapago<>6 ),0)as PorCobrar,
       IFNULL((SELECT SUM(abonado) from facturacion WHERE anulado = 0 and estado = 1 AND DATE(fechapago) BETWEEN DATE(fechaini) and DATE(fechafin) and moneda = 1),0)as TotalPagos,
       IFNULL((SELECT SUM(pendiente) from notaingreso WHERE anulado = 0 and estado = 1 and codTransaccion = 1
               AND DATE(fechapago) BETWEEN DATE(fechaini) and DATE(fechafin) and moneda = 1
        UNION 
        SELECT SUM(pendiente) from facturacion WHERE anulado = 0 and estado = 1 and codTransaccion IN (1,14)
               AND DATE(fechapago) BETWEEN DATE(fechaini) and DATE(fechafin) and moneda = 1),0)as PorPagar,
       IFNULL((SELECT SUM(fv.pendiente) FROM factura_venta fv INNER JOIN formapago fp ON fv.formapago=fp.codFormaPago WHERE fv.anulado = 0 and 
                fv.estado = 1 and DATE(fv.fechasalida) BETWEEN DATE(fechaini) and DATE(fechafin) and fp.tipoaccion = 1 and fv.formapago<>6 and fv.codSucursal=sucur and 
                fv.codTransaccion=7 and fv.moneda = 1),0.000)as VentasCredito,
       IFNULL((SELECT SUM(p.montocobrado) FROM factura_venta fv INNER JOIN formapago fp ON fv.formapago=fp.codFormaPago 
                 INNER JOIN pago p ON fv.codFacturaV=p.codNota WHERE fv.anulado = 0 and fv.estado = 1 and DATE(p.fechapago) BETWEEN DATE(fechaini) and DATE(fechafin) 
                   and fp.tipoaccion = 1 /*and fv.formapago=6*/ and fv.codSucursal=sucur and  fv.codTransaccion=7 and p.codTipoPago = 5
                   and p.ingresoegreso = 1 and p.estado = 1 /*and fv.moneda = 1*/and p.codMoneda = 1),0.000)as VentasEfectivo,
       IFNULL((SELECT SUM(p.montocobrado) FROM factura_venta fv INNER JOIN formapago fp ON fv.formapago=fp.codFormaPago 
                 INNER JOIN pago p ON fv.codFacturaV=p.codNota WHERE fv.anulado = 0 and fv.estado = 1
                   and fp.tipoaccion = 1 /*and fv.formapago=6*/ and fv.codSucursal=sucur and  fv.codTransaccion=7 and p.codTipoPago = 6
