/*-----------------------------------------------------------------------------------------*/
SELECT IFNULL(SUM(f.pendiente),0.00) INTO total_boleta_soles_credito FROM factura_venta f INNER JOIN formapago fp ON f.formapago = fp.codFormaPago
WHERE f.moneda = 1 and f.formapago <> 6 and fp.tipoaccion = 1 and f.estado = 1 
      and f.codSucursal=sucur and date(f.fechasalida) = date(fec) and f.codTipoDocumento = 1 and f.anulado = 0;
/*----------------------------------------------------------------------------------------------------------------------*/
SELECT IFNULL(SUM(f.abonado),0.00) INTO total_boleta_dolar_contado FROM factura_venta f INNER JOIN pago p ON f.codFacturaV = p.codNota 
    INNER JOIN formapago fp ON f.formapago = fp.codFormaPago
