WHERE f.moneda = 2 and f.formapago <> 6 and fp.tipoaccion = 1 and f.estado = 1 
      and f.codSucursal=sucur and date(f.fechasalida) = date(fec)
      and f.codTipoDocumento = 2 and f.anulado = 0;
/*-----------------------------------------------------------------------------------------------------*/
SELECT IFNULL(SUM(f.total),0.00) INTO total_notadebito_soles FROM factura_venta f 
WHERE f.moneda = 1 and f.estado = 1 and f.codSucursal=sucur and date(f.fechasalida) = date(fec) 
      and f.codTipoDocumento = 6 and f.anulado = 0;
