WHERE n.moneda = 2 and n.estado = 1 and n.anulado=0 and f.estado = 1 and f.anulado = 0 and n.codTransaccion = 20 and 
      DATE(f.fechacancelado) = DATE(fec) and n.aplicada = 1;
/*----------------------------------------------------------------------------------------------------*/
SELECT IFNULL(compra,0.00) INTO tcambio FROM tipocambio WHERE estado = 1 and DATE(fecha) = DATE(fec);
