     IF(IFNULL(c.ruc,'')='',c.nombre,c.razonsocial)as client, IF(p.codMoneda=1,p.montocobrado,0.00)as rc_sol_cont, 
     IF(p.codMoneda=2,p.montocobrado,0.00)as rc_dol_cont, suc.nombre as sucursal, tcambio
FROM factura_venta f INNER JOIN pago p ON f.codFacturaV = p.codNota
     INNER JOIN cliente c ON f.codCliente = c.codCliente
     INNER JOIN tipodocumento td ON p.codTipoDocumento = td.codTipoDocumento
		 INNER JOIN sucursal suc ON f.codSucursal = suc.codSucursal
WHERE f.fechasalida <> p.fechapago and p.codTipoPago = 5 and p.ingresoegreso = 1
      and date(p.fechapago) = date(fec) and f.codSucursal = sucur and f.formapago<>6;


