       INNER JOIN formapago fp ON f.formapago = fp.codFormaPago
WHERE f.formapago = 6 /*and f.moneda = 1*/ and p.codMoneda = 1 and f.estado = 1 and p.estado = 1 and fp.estado = 1 and fp.tipoaccion = 1 and p.codTipoPago = 8 
