       INNER JOIN formapago fp ON f.formapago = fp.codFormaPago
WHERE f.formapago <> 6 /*and f.moneda = 1*/ and p.codMoneda = 1 and f.estado = 1 and p.estado = 1 and fp.estado = 1 and fp.tipoaccion = 1 and p.codTipoPago = 8 
      and f.codSucursal = sucur and p.ingresoegreso = 1 and /*date(f.fechasalida) <> date(p.fechapago) and */
      date(p.fechapago) = date(fec);

SELECT SUM(p.montocobrado) INTO c_tarjeta_dolar FROM factura_venta f INNER JOIN pago p ON f.codFacturaV = p.codNota
       INNER JOIN formapago fp ON f.formapago = fp.codFormaPago
WHERE f.formapago <> 6 /*and f.moneda = 2*/ and p.codMoneda = 2 and f.estado = 1 and p.estado = 1 and fp.estado = 1 and fp.tipoaccion = 1 and p.codTipoPago = 8 
      and f.codSucursal = sucur and p.ingresoegreso = 1 and /*date(f.fechasalida) <> date(p.fechapago) and*/ 
