SELECT SUM(p.montocobrado) INTO c_transferencia_dolar FROM factura_venta f INNER JOIN pago p ON f.codFacturaV = p.codNota
       INNER JOIN formapago fp ON f.formapago = fp.codFormaPago
WHERE f.formapago <> 6 /*and f.moneda = 2*/ and p.codMoneda = 2 and f.estado = 1 and p.estado = 1 and fp.estado = 1 and fp.tipoaccion = 1 and p.codTipoPago = 9 
      and f.codSucursal = sucur and p.ingresoegreso = 1 /*and date(f.fechasalida) <> date(p.fechapago) */ and 
      date(p.fechapago) = date(fec) and p.Aprobado = 4;
/***************************************************NOTAS DE CREDITO*********************************************************************/
-- VENTAS
SELECT SUM(p.montocobrado) INTO nc_soles FROM factura_venta f INNER JOIN pago p ON f.codFacturaV = p.codNota
       INNER JOIN formapago fp ON f.formapago = fp.codFormaPago
WHERE f.formapago = 6 /*and f.moneda = 1*/ and p.codMoneda = 1 and f.estado = 1 and p.estado = 1 and fp.estado = 1 and fp.tipoaccion = 1 and p.codTipoPago = 10 
      and f.codSucursal = sucur and p.ingresoegreso = 1 and date(p.fechapago) = date(fec) /*and p.Aprobado = 4*/;

SELECT SUM(p.montocobrado) INTO nc_dolar FROM factura_venta f INNER JOIN pago p ON f.codFacturaV = p.codNota
       INNER JOIN formapago fp ON f.formapago = fp.codFormaPago
WHERE f.formapago = 6 /*and f.moneda = 2*/ and p.codMoneda = 2 and f.estado = 1 and p.estado = 1 and fp.estado = 1 and fp.tipoaccion = 1 and p.codTipoPago = 10 
      and f.codSucursal = sucur and p.ingresoegreso = 1 and date(p.fechapago) = date(fec) /*and p.Aprobado = 4*/;

-- COBRANZAS
SELECT SUM(p.montocobrado) INTO c_nc_soles FROM factura_venta f INNER JOIN pago p ON f.codFacturaV = p.codNota
       INNER JOIN formapago fp ON f.formapago = fp.codFormaPago
WHERE f.formapago <> 6 /*and f.moneda = 1*/ and p.codMoneda = 1 and f.estado = 1 and p.estado = 1 and fp.estado = 1 and fp.tipoaccion = 1 and p.codTipoPago = 10 
      and f.codSucursal = sucur and p.ingresoegreso = 1 and /*date(f.fechasalida) <> date(p.fechapago) and */
      date(p.fechapago) = date(fec) /*and p.Aprobado = 4*/;

SELECT SUM(p.montocobrado) INTO c_nc_dolar FROM factura_venta f INNER JOIN pago p ON f.codFacturaV = p.codNota
       INNER JOIN formapago fp ON f.formapago = fp.codFormaPago
WHERE f.formapago <> 6 /*and f.moneda = 2*/ and p.codMoneda = 2 and f.estado = 1 and p.estado = 1 and fp.estado = 1 and fp.tipoaccion = 1 and p.codTipoPago = 10 
      and f.codSucursal = sucur and p.ingresoegreso = 1 and /*date(f.fechasalida) <> date(p.fechapago) and */
      date(p.fechapago) = date(fec) /*and p.Aprobado = 4*/;
/****************************************************** CREDITO *************************************************************************/
SELECT SUM(f.pendiente) INTO credito_soles FROM factura_venta f INNER JOIN formapago fp ON f.formapago = fp.codFormaPago
WHERE f.formapago <> 6 and date(f.fechasalida) = date(fec) and f.estado = 1 and f.anulado = 0
      and fp.tipoaccion = 1 and fp.estado = 1 and f.codSucursal = sucur and f.moneda = 1;

SELECT SUM(f.pendiente) INTO credito_dolar FROM factura_venta f INNER JOIN formapago fp ON f.formapago = fp.codFormaPago
WHERE f.formapago <> 6 and date(f.fechasalida) = date(fec) and f.estado = 1 and f.anulado = 0
      and fp.tipoaccion = 1 and fp.estado = 1 and f.codSucursal = sucur and f.moneda = 2;
/************************************************* MUESTRA REPORTE **********************************************************************/

SELECT IFNULL(efectivo_soles,0.00)as v_soles_efectivo, IFNULL(efectivo_dolar,0.00)as v_dolar_efectivo, 
       IFNULL(deposito_soles,0.00)as v_soles_dep, IFNULL(deposito_dolar,0.00)as v_dolar_dep, IFNULL(cheque_soles,0.00)as v_soles_cheque, 
       IFNULL(cheque_dolar,0.00)as v_dolar_cheque, IFNULL(tarjeta_soles,0.00)as v_soles_tarjeta, 
       IFNULL(tarjeta_dolar,0.00)as v_dolar_tarjeta, IFNULL(transferencia_soles,0.00)as v_soles_tran, 
       IFNULL(transferencia_dolar,0.00)as v_dolar_tran, IFNULL(nc_soles,0.00)as v_soles_nc, IFNULL(nc_dolar,0.00)as v_dolar_nc,
       IFNULL(c_efectivo_soles,0.00)as c_soles_efectivo, IFNULL(c_efectivo_dolar,0.00)as c_dolar_efectivo, 
       IFNULL(c_deposito_soles,0.00)as c_soles_dep, IFNULL(c_deposito_dolar,0.00)as c_dolar_dep, 
       IFNULL(c_cheque_soles,0.00)as c_soles_cheque, IFNULL(c_cheque_dolar,0.00)as c_dolar_cheque, 
       IFNULL(c_tarjeta_soles,0.00)as c_soles_tarjeta, IFNULL(c_tarjeta_dolar,0.00)as c_dolar_tarjeta, 
       IFNULL(c_transferencia_soles,0.00)as c_soles_tran, IFNULL(c_transferencia_dolar,0.00)as c_dolar_tran, 
       IFNULL(c_nc_soles,0.00)as c_soles_nc, IFNULL(c_nc_dolar,0.00)as c_dolar_nc , v_tc as tipoCambio, v_sucur as d_sucursal,
       fec as fecha, IFNULL(credito_soles,0.00)as soles_credito, IFNULL(credito_dolar,0.00)as dolar_credito;

END
;;
DELIMITER ;

