-- ----------------------------
DROP PROCEDURE IF EXISTS `ReportMercaderiaPorEntregar`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ReportMercaderiaPorEntregar`(fecini datetime, fecfin datetime, alma int(11))
BEGIN

SELECT DISTINCT nt.codNotaSalida, DATE_FORMAT(nt.fechasalida,'%d-%m-%Y')as fechasalida, CONCAT(td.sigla,'-',nt.numdocumento)as numdoc, 
					 (SELECT SUBSTRING(nombre,9,30) FROM almacen WHERE codAlmacen=alma)as nomalmacen, 
           (SELECT cantidad FROM detallefactura_venta WHERE codFacturaV=f.codFacturaV and codProducto=dt.codProducto 
                          AND codAlmacen=alma)as CantOrigen, IFNULL(dt.cantidadpendiente,0.00)as CantPorEntregar,
           IFNULL(dt.cantidad,0.00)as CantDespachada, IFNULL(cl.ruc,cl.dni)as rucdni_cli, 
           IF(IFNULL(cl.nombre,'')='',cl.razonsocial,cl.nombre)as Cliente, p.referencia, p.descripcion as Producto,
           DATE_FORMAT(fecini,'%d-%m-%Y')as fechaini, DATE_FORMAT(fecfin,'%d-%m-%Y')as fecfin
		FROM notasalida nt INNER JOIN detallenotasalida dt ON nt.codNotaSalida=dt.codNotaSalida
				 INNER JOIN tipodocumento td ON td.codTipoDocumento=nt.codTipoDocumento
         LEFT OUTER JOIN factura_venta f ON f.codFacturaV=nt.documentoreferencia
         INNER JOIN cliente cl ON f.codCliente = cl.codCliente
         INNER JOIN producto p ON p.codProducto = dt.codProducto
		WHERE dt.cantidad <> (SELECT cantidad FROM detallefactura_venta WHERE codFacturaV=f.codFacturaV and codProducto=dt.codProducto 
                          AND codAlmacen=alma) 
          AND f.serie = (SELECT s.serie FROM serie s WHERE (s.preimpreso=0 or s.preimpreso=1) AND s.codSerie = f.codSerie)
          AND dt.codProducto = (SELECT pa.codProducto FROM productoalmacen pa WHERE pa.codAlmacen=alma 
                                AND pa.codProducto= dt.codProducto AND pa.stockporentregar > 0)
          AND td.codTipoDocumento=1 OR td.codTipoDocumento=2 and DATE(nt.fechasalida) BETWEEN DATE(fecini) and DATE(fecfin);

END
;;
DELIMITER ;

