DROP PROCEDURE IF EXISTS `ReportNotaCreditoCompra`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ReportNotaCreditoCompra`(codnota int(11), codalma int(11))
BEGIN
	SELECT 
	nt.codNotaSalida AS codNota, pv.ruc, IFNULL(pv.razonsocial,'') AS proveedor, IFNULL(pv.direccion,'') AS dirprov,
	DATE_FORMAT(nt.fechasalida,'%d-%m-%Y') AS fecha, fc.codTipoDocumento, IF(fc.codTipoDocumento = 1,'Boleta de Venta','Factura') AS documento,
	fc.DocumentoFactura as ndocref, DATE_FORMAT(fc.fecharegistro,'%d-%m-%Y')as fecharegistro,IFNULL(dnt.cantidad,0) as cantidad,
	IFNULL(dnt.preciounitario,0) as preciounitario, IFNULL(UPPER(nt.motivo),'')as motivo, 
  IFNULL(dnt.subtotal,0) as valorventadet, IFNULL(dnt.igv,0) as igvdet,  IFNULL(dnt.importe,0) as totaldet,
  dnt.codProducto, IFNULL(p.referencia,'')as referencia, IFNULL(p.descripcion,'')as producto, dnt.unidadingresada,
  IFNULL(u.descripcion,'')as unidad, IFNULL(u.sigla,'')as siguni, nt.igv, nt.bruto as valorventa, nt.total
FROM
	detallenotasalida dnt INNER JOIN notasalida nt ON dnt.codNotaSalida = nt.codNotaSalida
	INNER JOIN facturacion fc ON nt.codAplicada = fc.codFactura
	INNER JOIN proveedor pv ON fc.codProveedor = pv.codProveedor
	INNER JOIN producto p ON dnt.codProducto = p.codProducto
	INNER JOIN tipodocumento td ON fc.codTipoDocumento = td.codTipoDocumento
	INNER JOIN unidadmedida u ON dnt.unidadingresada = u.codUnidadMedida
WHERE 
	nt.codNotaSalida = codnota AND nt.estado = 1 AND fc.estado = 1 AND nt.codTransaccion = 19 AND nt.codAlmacen = codalma;
END
;;
DELIMITER ;

