DROP PROCEDURE IF EXISTS `ReportNotaCreditoVenta`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ReportNotaCreditoVenta`(codnota int(11), codalma int(11))
BEGIN
DECLARE grecia BLOB;

SELECT
nc.codNotaI AS NC_codNotaI, 
nc.codNotaCredito AS NC_codNotaCredito,
nc.bruto AS NC_bruto, 
nc.igv AS NC_igv, 
nc.total AS NC_total,
nc.codReferencia AS NC_documentoafectado,
fv.formapago AS NC_formapago,
fv.fechapago AS NC_fechapago,
fp.descripcion  AS NC_condiciompago,
(CASE fv.codTipoDocumento
WHEN 1 THEN CONCAT("B" ,fv.serie," - ", LPAD(fv.numDocumento,8,0) )
WHEN 2 THEN  CONCAT("F" ,fv.serie," - ", LPAD(fv.numDocumento,8,0) ) END) as NC_numDocumentoRef,
/*CONCAT("F" , nc.serie,' - ',LPAD(nc.DocumentoFactura,8,0)) as NC_numDocumento,*/
/*CONCAT("NC-",*nc.DocumentoFactura*) as NC_numDocumento,*/
(CASE fv.codTipoDocumento
WHEN 1 THEN  CONCAT("B" , nc.serie,' - ',LPAD(nc.DocumentoFactura,8,0)) 
WHEN 2 THEN  CONCAT("F" , nc.serie,' - ',LPAD(nc.DocumentoFactura,8,0)) 
END)as NC_numDocumento,


nc.fecharegistro AS NC_fechaemision,
IFNULL(UPPER(nc.motivo),'') as NC_motivo,
IFNULL(UPPER(tnc.denominacion),'') AS NC_MOTIVO_DESCRIPCION,
nc.gravadas as NC_gravadas,
nc.exoneradas as NC_exoneradas,
nc.inafectas as NC_inafectas,
nc.gratuitas as NC_gratuitas,
mon.descripcion AS NC_moneda,

emp.razonsocial AS empresa_razonsocial,
emp.direccion AS empresa_direccion, 
emp.ruc AS empresa_ruc, 

suc.descripcion as sucursal_descripcion,
suc.telefono as sucursal_telefono,
suc.ubicacion as sucursal_ubicacion,
 
cl.codCliente AS cli_codCliente, 
IFNULL(cl.codigopersonalizado,'') AS cli_codpercli, 
IF(fv.codTipoDocumento=1,cl.nombre, cl.razonsocial)as cli_nombrerazon, 
(CASE fv.codTipoDocumento
WHEN 1 THEN  cl.dni 
WHEN 2 THEN  cl.ruc
END) as cli_dniruc,
fv.codTipoDocumento,
cl.direccionlegal AS cli_direccionlegal,


DATE_FORMAT(fv.fechasalida,'%d-%m-%Y')as fechasalida, 
IFNULL(dnc.cantidad,0) as DNC_cantidad, 
IFNULL(dnc.preciounitario,0) as DNC_preciounitario, 
 
IFNULL(dnc.subtotal,0) as DNC_valorventadet, 
IFNULL(dnc.igv,0)as DNC_igv,  
IFNULL(dnc.importe,0)as DNC_total, 
IFNULL(dnc.montodscto,0) AS DNC_descuebto,

dnc.codProducto as DNC_codProducto, 
IFNULL(p.referencia,'') as DNC_referencia, 
IF(dnc.codProducto != 0 ,IFNULL(p.descripcion,''),'') as DNC_producto, 
dnc.unidadingresada AS DNC_unidadingresada,
IFNULL(u.descripcion,'') as DNC_unidad, 
IFNULL(u.sigla,'') as DNC_siguni,
usu.nombre as cajero,
grecia AS firma 
       
FROM detallenotacredito dnc 
INNER JOIN notacredito nc ON dnc.codNotaCredito = nc.codNotaCredito
INNER JOIN factura_venta fv ON nc.codReferencia = fv.codFacturaV 
INNER JOIN cliente cl ON cl.codCliente = fv.codCliente
INNER JOIN formapago fp ON fp.codFormaPago = fv.formapago
INNER JOIN tipo_nota_credito tnc ON tnc.codigosunat = nc.motivo
LEFT JOIN tipodocumento td ON fv.codTipoDocumento = td.codTipoDocumento 
LEFT JOIN producto p ON dnc.codProducto = p.codProducto
LEFT JOIN unidadmedida u ON dnc.unidadingresada = u.codUnidadMedida
LEFT JOIN almacen al ON al.codAlmacen = dnc.codAlmacen 
LEFT JOIN empresa emp ON emp.codEmpresa = al.codEmpresa
LEFT JOIN moneda mon on mon.codMoneda=fv.moneda
LEFT JOIN sucursal suc ON suc.codSucursal = al.codSucursal
LEFT JOIN vendedor v ON v.codVendedor = fv.codVendedor 
LEFT JOIN usuario usu ON dnc.codUser = usu.codUser

WHERE nc.codNotaCredito = codnota 
AND nc.estado = 1 
AND dnc.estado = 1 
AND fv.estado = 1 
AND nc.codTransaccion = 20 
AND nc.codAlmacen = codalma;

END
;;
DELIMITER ;

