	IF(IFNULL(fc.motivo,'')<>('Ajuste por diferencia precio'),'',IFNULL(dfc.cantidad,0)) AS cantidad,
	IF(IFNULL(fc.motivo,'')<>'Ajuste por diferencia precio','',IFNULL(dfc.preciounitario,0))as preciouni,
	IFNULL(dfc.subtotal,0)as valorventadet, IFNULL(dfc.igv,0) as igvdet, IFNULL(dfc.importe,0) as totaldet, dfc.codProducto, IFNULL(fc.bruto,0) as valorventa, 
  IFNULL(fc.igv,0)as igv, IFNULL(fc.total,0) as total,
	IF(IFNULL(fc.motivo,'')<>'Ajuste por diferencia precio','', IFNULL(p.referencia,''))as referencia, p.descripcion, dfc.unidadingresada,
       u.descripcion as unidad, fc.codUsuario, 
       CONCAT(usu.nombre,' ',usu.apellido)as usuario, fc.codAlmacen, al.nombre as almacen	

FROM 
	facturacion fc INNER JOIN detallefacturacion dfc ON fc.codFactura = dfc.codFactura
	INNER JOIN proveedor pv ON fc.codProveedor = pv.codProveedor
	INNER JOIN moneda m ON fc.moneda = m.codMoneda
	INNER JOIN tipodocumento td ON fc.codTipoDocumento=td.codTipoDocumento
	INNER JOIN producto p ON dfc.codProducto = p.codProducto
	INNER JOIN unidadmedida u ON dfc.unidadingresada=u.codUnidadMedida
	INNER JOIN usuario usu ON usu.codUsuario=fc.codUsuario
	INNER JOIN almacen al ON fc.codAlmacen=al.codAlmacen
WHERE fc.codFactura = codnota AND fc.estado = 1 AND dfc.estado=1 AND fc.codAlmacen = codalma AND fc.codTransaccion = 18;
END
;;
