-- ----------------------------
-- Procedure structure for ReportRankingVentaxArticulo
-- ----------------------------
DROP PROCEDURE IF EXISTS `ReportRankingVentaxArticulo`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ReportRankingVentaxArticulo`(fecha1 datetime, fecha2 datetime, codalma int(11))
BEGIN

DECLARE v_sum DECIMAL(10,4);

SELECT SUM(f.total-f.igv) INTO v_sum FROM factura_venta f WHERE DATE(f.fechasalida) BETWEEN DATE(fecha1) AND DATE(fecha2) 
        and f.codAlmacen=codalma;

SELECT dns.codProducto, p.referencia, p.descripcion as producto, sum(dns.cantidad)as cantidad,
       (SUM(dns.cantidad*dns.valoreal)/SUM(dns.cantidad))as p_unit, (SUM(dns.cantidad*dns.valoreal))as valor_venta, 
       (SUM(dns.cantidad*dns.valorpromedio))as costo_venta, 
       (((SUM(dns.cantidad*dns.valoreal))- SUM(dns.cantidad*dns.valorpromedio)))as util,
  ((((SUM(dns.cantidad*dns.valoreal))- SUM(dns.cantidad*dns.valorpromedio))/(SUM(dns.cantidad*dns.valorpromedio)))*100)as pct_util,
  DATE_FORMAT(ns.fechasalida,'%d-%m-%Y')as fechasalida, DATE_FORMAT(fecha1,'%d-%m-%Y')as fecha1, DATE_FORMAT(fecha2,'%d-%m-%Y')as fecha2,
  ((SUM(dns.cantidad*dns.valoreal)*100)/(v_sum))AS pct_vv
FROM detallefactura_venta dns INNER JOIN producto p ON dns.codProducto=p.codProducto
     INNER JOIN factura_venta ns ON dns.codFacturaV=ns.codFacturaV
WHERE DATE(ns.fechasalida) BETWEEN DATE(fecha1) AND DATE(fecha2) and ns.codTransaccion=7 and ns.codAlmacen=codalma and 
      ns.estado = 1 and dns.estado = 1 and ns.anulado = 0 and dns.anulado = 0
GROUP BY dns.codProducto
ORDER BY valor_venta DESC;

END
;;
DELIMITER ;

