CREATE DEFINER=`root`@`localhost` PROCEDURE `ReportRankingVentaxCliente`(fecha1 datetime, fecha2 datetime, codalma int(11))
BEGIN
DECLARE v_sum DECIMAL(10,4);

SELECT SUM(f.total-f.igv) INTO v_sum FROM factura_venta f WHERE DATE(f.fechasalida) BETWEEN DATE(fecha1) AND DATE(fecha2) 
        and f.codAlmacen=codalma;

SELECT ns.codCliente as cli, c.codigopersonalizado as codpercli, IFNULL(c.ruc,c.dni)as dniruc_cli, 
IF(c.nombre='',c.razonsocial,c.nombre)as nombre, (SUM(dns.cantidad*dns.valoreal))as valor_venta, (SUM(dns.igv))as igv,
  (SUM(dns.cantidad*dns.valoreal) + SUM(dns.igv))as total_venta, (SUM(dns.cantidad*dns.valorpromedio))as costo_venta,
  (SUM(dns.cantidad*dns.valoreal) - SUM(dns.cantidad*dns.valorpromedio))as util,
  ((((SUM(dns.cantidad*dns.valoreal)) - SUM(dns.cantidad*dns.valorpromedio))/(SUM(dns.cantidad*dns.valoreal)))*100)as pct_util,
  ((SUM(dns.cantidad*dns.valoreal)*100)/(v_sum))AS pct_vv,
DATE_FORMAT(ns.fechasalida,'%d-%m-%Y')as fechasalida, DATE_FORMAT(fecha1,'%d-%m-%Y')as fecha1, DATE_FORMAT(fecha2,'%d-%m-%Y')as fecha2
