DROP PROCEDURE IF EXISTS `ReportRotacionProducto`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ReportRotacionProducto`(alma int(11), cri int(11), fecha1 datetime, fecha2 datetime, mes1 int(11), mes2 int(11), annio varchar(7), codalma int(11))
BEGIN

-- alma: 1 = Todos los Almacenes - 0 = Solo Un Almacen
-- cri: 1 = Por Fechas - 0 = Por mes 

IF cri = 1 THEN
   IF alma = 1 THEN
      SELECT DISTINCT pa.codProducto, p.referencia, p.descripcion, al.nombre as alma, pa.stockactual as stock
      FROM producto p INNER JOIN productoalmacen pa ON p.codProducto=pa.codProducto
           INNER JOIN almacen al ON pa.codAlmacen=al.codAlmacen
           LEFT JOIN factura_venta f ON f.codAlmacen = al.codAlmacen
			WHERE pa.codProducto NOT IN (SELECT codProducto FROM detallefactura_venta WHERE codProducto=pa.codProducto and codAlmacen=pa.codAlmacen)
            AND DATE(f.fechasalida) BETWEEN DATE(fecha1) and DATE(fecha2) and p.estado = 1 and f.estado = 1 and al.estado = 1
      ORDER BY p.descripcion;
   ELSE
      SELECT DISTINCT pa.codProducto, p.referencia, p.descripcion, al.nombre as alma, pa.stockactual as stock
      FROM producto p INNER JOIN productoalmacen pa ON p.codProducto=pa.codProducto
           INNER JOIN almacen al ON pa.codAlmacen=al.codAlmacen
           LEFT JOIN factura_venta f ON f.codAlmacen = al.codAlmacen
			WHERE pa.codProducto NOT IN (SELECT codProducto FROM detallefactura_venta WHERE codProducto=pa.codProducto and codAlmacen=pa.codAlmacen)
            AND DATE(f.fechasalida) BETWEEN DATE(fecha1) and DATE(fecha2) and p.estado = 1 and f.estado = 1 and al.estado=1
            and al.codAlmacen = codalma
      ORDER BY p.descripcion;
   END IF;
ELSEIF cri = 0 THEN
    IF alma = 1 THEN
			 SELECT DISTINCT pa.codProducto, p.referencia, p.descripcion, al.nombre as alma, pa.stockactual as stock
       FROM producto p INNER JOIN productoalmacen pa ON p.codProducto=pa.codProducto
            INNER JOIN almacen al ON pa.codAlmacen=al.codAlmacen
            LEFT JOIN factura_venta f ON f.codAlmacen = al.codAlmacen
			 WHERE pa.codProducto NOT IN (SELECT codProducto FROM detallefactura_venta WHERE codProducto=pa.codProducto and codAlmacen=pa.codAlmacen)
             AND MONTH(f.fechasalida) BETWEEN mes1 and mes2 and p.estado = 1 and f.estado = 1 and al.estado = 1 and 
             YEAR(f.fechasalida) = annio
       ORDER BY p.descripcion;
    ELSE
       SELECT DISTINCT pa.codProducto, p.referencia, p.descripcion, al.nombre as alma, pa.stockactual as stock
       FROM producto p INNER JOIN productoalmacen pa ON p.codProducto=pa.codProducto
           INNER JOIN almacen al ON pa.codAlmacen=al.codAlmacen
           LEFT JOIN factura_venta f ON f.codAlmacen = al.codAlmacen
			 WHERE pa.codProducto NOT IN (SELECT codProducto FROM detallefactura_venta WHERE codProducto=pa.codProducto and codAlmacen=pa.codAlmacen)
            AND MONTH(f.fechasalida) BETWEEN mes1 and mes2 and p.estado = 1 and f.estado = 1 and al.estado=1 and YEAR(f.fechasalida) = annio
            and al.codAlmacen = codalma
       ORDER BY p.descripcion;
   END IF;
END IF;  
END
;;
DELIMITER ;

