	ON vd.codVendedor = n.codVendedor WHERE n.codVendedor = cod AND f.tipo = 0 AND n.anulado = 0 AND n.estado = 1 AND DATE(n.fechasalida) BETWEEN DATE(fecha1) AND DATE(fecha2)and n.moneda=2 and n.formapago=forma),0) AS creditodolares,
	prd.referencia,prd.descripcion,ds.cantidad,ds.preciounitario,ds.igv,ds.valoreal,ds.importe
	
	
  FROM factura_venta fv LEFT OUTER JOIN detallefactura_venta ds ON ds.codFacturaV=fv.codFacturaV
		INNER JOIN producto prd ON prd.codProducto=ds.codProducto	
     LEFT JOIN vendedor v ON fv.codVendedor=v.codVendedor 
     LEFT JOIN formapago fp ON fp.codFormaPago = fv.formapago	
     LEFT JOIN almacen al ON fv.codAlmacen = al.codAlmacen 
     LEFT JOIN empresa e ON e.codEmpresa = al.codEmpresa 
     LEFT JOIN cliente c ON c.codCliente = fv.codCliente 
     LEFT JOIN tipodocumento td ON td.codTipoDocumento = fv.codTipoDocumento 
     LEFT JOIN zona z ON c.zona = z.codZona
     LEFT JOIN sucursal su ON fv.codSucursal=su.codSucursal
     LEFT JOIN serie s ON fv.codSerie=s.codSerie

	WHERE fv.codVendedor = cod AND al.codAlmacen = empre AND fv.anulado = 0 AND fv.estado = 1 AND DATE(fv.fechasalida) BETWEEN DATE(fecha1) AND DATE(fecha2)
	-- GROUP BY fv.codFacturaV
	ORDER BY 12 ASC;

ELSEIF cod != 0 AND forma != 0 THEN

	SELECT fv.codCliente,c.codigopersonalizado, IFNULL(c.ruc,c.dni)AS ruc_dni, c.nombre, c.razonsocial, td.sigla, CONCAT(td.sigla,"-",s.serie,"-",fv.numDocumento) AS num_doc, fv.total,
	CASE forma WHEN fp.codFormaPago THEN fp.descripcion END AS cabecera, IFNULL(v.codVendedor,0)AS codVendedor, IFNULL(v.dni,"")as dniVen, IFNULL(v.apellido,"Sin Vendedor")AS nomVen, DATE_FORMAT(fv.fechasalida,'%d-%m-%Y') AS fechasalida, DATE_FORMAT(fv.fechapago,'%d-%m-%Y') AS fechapago,
	IF(c.zona = 0,"TODOS",z.descripcion) AS zona,z.descripcion AS zona1,DATE_FORMAT(fecha1,'%d-%m-%Y') AS fecha1, DATE_FORMAT(fecha2,'%d-%m-%Y') AS fecha2, e.razonsocial,
	(SELECT SUM(n.total) FROM factura_venta n LEFT JOIN formapago f ON f.codFormaPago = n.formapago LEFT JOIN cliente cl ON cl.codCliente = n.codCliente WHERE n.codVendedor = cod AND n.formapago = forma AND f.tipo = 1 AND n.anulado = 0 AND n.estado = 1 AND DATE(n.fechasalida) BETWEEN DATE(fecha1) AND DATE(fecha2)) AS contado,
	(SELECT SUM(n.total) FROM factura_venta n LEFT JOIN formapago f ON f.codFormaPago = n.formapago LEFT JOIN cliente cl ON cl.codCliente = n.codCliente WHERE n.codVendedor = cod AND n.formapago = forma AND f.tipo = 0 AND n.anulado = 0 AND n.estado = 1 AND DATE(n.fechasalida) BETWEEN DATE(fecha1) AND DATE(fecha2)) AS credito,
	prd.referencia,prd.descripcion,ds.cantidad,ds.preciounitario,ds.igv,ds.valoreal,ds.importe
	
  FROM factura_venta fv LEFT OUTER JOIN detallefactura_venta ds ON ds.codFacturaV=fv.codFacturaV
		INNER JOIN producto prd ON prd.codProducto=ds.codProducto	
     LEFT JOIN vendedor v ON fv.codVendedor=v.codVendedor 
     LEFT JOIN formapago fp ON fp.codFormaPago = fv.formapago	
     LEFT JOIN almacen al ON fv.codAlmacen = al.codAlmacen 
     LEFT JOIN empresa e ON e.codEmpresa = al.codEmpresa 
     LEFT JOIN cliente c ON c.codCliente = fv.codCliente 
     LEFT JOIN tipodocumento td ON td.codTipoDocumento = fv.codTipoDocumento 
     LEFT JOIN zona z ON c.zona = z.codZona
     LEFT JOIN sucursal su ON fv.codSucursal=su.codSucursal
     LEFT JOIN serie s ON fv.codSerie=s.codSerie
	
  WHERE fv.codVendedor = cod AND fv.formapago = forma AND al.codAlmacen = empre AND fv.anulado = 0 AND fv.estado = 1 AND DATE(fv.fechasalida) BETWEEN DATE(fecha1) AND DATE(fecha2)
	-- GROUP BY fv.codFacturaV
	ORDER BY 12 ASC;

END IF;
END
;;
